,p_computation_item=>'P67_SQL'
,p_computation_type=>'FUNCTION_BODY'
,p_computation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_sql         varchar2(4000);',
'  q             varchar2(255);',
'  ',
'  la_fk         wwv_flow_global.vc_arr2;',
'  l_fk          varchar2(4000) := null;',
'  la_fk_columns wwv_flow_global.vc_arr2;',
'  l_fk_columns  varchar2(4000) := null;',
'begin',
'  if :P79_CONSTRAINT_TYPE = ''PK'' then',
'    q := ''"''||:P79_CONSTRAINT_NAME||''"''||',
'       '' primary key ("''||:p79_pk1||''"'';',
'    if :p79_pk2 != ''0'' then',
'       q := q||'', "''||:p79_pk2||''"'';',
'    end if;',
'    if :p79_pk3 != ''0'' then',
'       q := q||'', "''||:p79_pk3||''"'';',
'    end if;',
'    q := q||'')'';',
'  elsif :P79_CONSTRAINT_TYPE = ''FK'' then',
'    la_fk := wwv_flow_utilities.string_to_table2(:p79_fk);',
'    la_fk_columns := wwv_flow_utilities.string_to_table2(:p79_fk_columns);',
'    ',
'    for i in 1..la_fk.count',
'    loop',
'      if i > 1 then',
'        l_fk := l_fk||'','';',
'      end if;',
'      l_fk := l_fk||''"''||la_fk(i)||''"'';',
'    end loop;',
'    for i in 1..la_fk_columns.count',
'    loop',
'      if i > 1 then',
'        l_fk_columns := l_fk_columns||'','';',
'      end if;',
'      l_fk_columns := l_fk_columns||''"''||la_fk_columns(i)||''"'';',
'    end loop;',
'    ',
'    q := ''"''||:P79_CONSTRAINT_NAME||''" foreign key (''||',
'         l_fk||'') references "''||:p79_fk_table||''" (''||',
'         l_fk_columns||'')'';  ',
'    if :P79_CASCADE = ''C'' then',
'      q := q||'' on delete cascade'';',
'    elsif :P79_CASCADE = ''N'' then',
'      q := q||'' on delete set null'';',
