wwv_flow_api.create_page_process(
 p_id=>wwv_flow_api.id(22516125575777686.4501)
,p_process_sequence=>10
,p_process_point=>'AFTER_HEADER'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'set type'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare ',
'    l_obj varchar2(4000);',
'    l_own varchar2(4000);',
'    l_perm number;',
'    x pls_integer;',
'    l_exit boolean;',
'    l_found boolean;',
'begin',
'    l_obj        := rtrim(ltrim(:P139_OBJECT));',
'    l_own        := :P1003_SCHEMA;',
'    :P139_STATUS := null;',
'    :P139_TYPE   := null;',
'  if (:P139_ACTION = ''GET'') then',
'     :P139_OWNER  := null;',
'     for c in (select decode(object_type,''PACKAGE BODY'',''PACKAGE'',object_type) object_type, status',
'               from sys.dba_objects ',
'               where object_name = l_obj',
'               and owner = :P1003_SCHEMA ) loop',
'      :P139_TYPE := c.object_type;',
'      :P139_STATUS := c.status;',
'      :P139_OWNER  := :P1003_SCHEMA;',
'    end loop;',
'    ',
'    x := 0; -- arbitrary limiter to prevent infinite loops',
'    while (:P139_TYPE = ''SYNONYM'' and x < 100) loop',
'      x := x + 1;',
'      for c in (select table_name, table_owner ',
'                from sys.dba_synonyms ',
'                where synonym_name = :P139_OBJECT',
'                and owner = :P1003_SCHEMA) loop',
'        l_obj := c.TABLE_NAME;',
'        l_own := c.TABLE_OWNER;',
'      end loop;',
'      ',
'      l_found := false;',
'      for c in (select decode(object_type,''PACKAGE BODY'',''PACKAGE'',object_type) object_type, status, object_name, owner',
'                from sys.dba_objects ',
'                where object_name = l_obj ',
'                and owner = l_own ) loop',
'            l_found := true;',
'            :P139_TYPE   := c.object_type;',
'            :P139_STATUS := c.status;',
'            :P139_OBJECT := c.object_name;',
'            :P139_OWNER  := c.owner; ',
'      end loop;        ',
'    end loop;',
'    ',
'    if not l_found then',
'      :P139_TYPE := null;',
'    end if;',
'    ',
'    if (:P139_TYPE is null) then',
'      for c in (select table_name, table_owner ',
'                from sys.dba_synonyms ',
'                where synonym_name = :P139_OBJECT',
'                and (owner = ''PUBLIC''',
'                or (table_owner is null and db_link is not null))) loop',
'         l_obj := c.TABLE_NAME;',
'         l_own := c.TABLE_OWNER;',
'         :P139_TYPE := ''SYNONYM'';',
'      end loop;',
'      ',
'      x := 0;',
'      l_exit := false;',
'      while (:P139_TYPE = ''SYNONYM'' and x < 100) loop',
'        x := x + 1;',
'        l_found := false;',
'        for c in (select table_name, table_owner',
'                  from sys.dba_synonyms ',
'                  where synonym_name = :P139_OBJECT',
'                  and owner = l_own',
'                  and db_link is null) loop          ',
'          l_obj := c.TABLE_NAME;',
'          l_own := c.TABLE_OWNER;',
'          l_found := true;',
'        end loop;',
'        if not l_found then',
'				  for c in (select table_name, db_link',
'                  from sys.dba_synonyms ',
'                  where synonym_name = :P139_OBJECT',
'                  and table_owner is null',
'                  and db_link is not null) loop            ',
'            :P139_OBJECT := c.TABLE_NAME||''@''||c.db_link;',
'            l_exit := true;',
'            exit;',
'          end loop;',
'        end if;',
'        if l_exit then',
'          exit;',
'        else',
'          for c in (select decode(object_type,''PACKAGE BODY'',''PACKAGE'',object_type) object_type, status, object_name, owner',
'                    from sys.dba_objects ',
'                    where object_name = l_obj ',
'                    and owner = l_own ) loop',
'              :P139_TYPE   := c.object_type;',
'              :P139_STATUS := c.status;',
'              :P139_OBJECT := c.object_name;',
'              :P139_OWNER  := c.owner;',
'          end loop;',
'        end if;  ',
'       end loop;',
'    end if;',
'  else',
'',
'    for c in (select decode(object_type,''PACKAGE BODY'',''PACKAGE'',object_type) object_type, status',
'              from sys.dba_objects ',
'              where object_name = :P139_OBJECT ',
'              and owner = :P139_OWNER ) loop',
'        :P139_TYPE := c.object_type;',
'        :P139_STATUS := c.status;',
'    end loop;',
'  end if;',
'',
'  select count(*) into l_perm ',
'  from sys.dba_tab_privs ',
'  where ( grantee in (''PUBLIC'',:P1003_SCHEMA) or grantee in ',
'      (select granted_role from  sys.dba_role_privs where grantee = :P1003_SCHEMA))',
'  and table_name = :P139_OBJECT;',
'',
'  if (l_perm = 0 and :P139_OWNER != :P1003_SCHEMA) then',
'    :P139_OBJECT := null;',
'    :P139_TYPE := null;',
'  end if;',
'',
'  if (:P139_TYPE is null) then',
'    :P139_TYPE := ''---'';',
'  end if;',
'  if (:P139_OBJECT is null) then',
'    :P139_OBJECT:= ''---'';',
'  end if;',
'',
'end;'))
);
end;
