wwv_flow_api.create_page_validation(
 p_id=>wwv_flow_api.id(228353121517062989.4501)
,p_validation_name=>'Foreign key match'
,p_validation_sequence=>10
,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare ',
'l_fk_string wwv_flow_global.vc_arr2;',
'l_pk_string wwv_flow_global.vc_arr2;',
'l_data_type_count number;',
'l_p_count number;',
'',
'begin',
'for c1 in (select seq_id,c002,c003,c004',
'                 from wwv_flow_collections        ',
'                where collection_name = ''TBL_WIZ_FKS''',
'                order by seq_id)',
'',
'loop',
'l_fk_string := wwv_flow_utilities.string_to_table(to_char(c1.c002),'','');',
'l_pk_string := wwv_flow_utilities.string_to_table(to_char(c1.c004),'','');',
'',
'select count(*) into l_p_count',
'  from sys.dba_cons_columns',
'where table_name = c1.c003',
'  and owner = :P145_SCHEMA',
'  and constraint_name = (select constraint_name',
'                           from sys.dba_constraints',
'                          where table_name = c1.c003',
'                            and owner = :P145_SCHEMA',
'                            and constraint_type = ''P'');',
'',
'if l_fk_string.count <> l_p_count then',
'   return false;',
'end if;',
'',
'for i in 1..l_fk_string.count',
'loop',
'      for c2 in (select c001,C002',
'                 from wwv_flow_collections        ',
'                where collection_name = ''TBL_WIZ_COLUMNS''',
'                 and c001 = l_fk_string(i)',
'                order by seq_id)',
'     loop',
'          select count(data_type) into l_data_type_count',
'              from sys.dba_tab_columns ',
'              where table_name = c1.c003',
'              and owner = :P145_SCHEMA',
'              and column_name = l_pk_string(i)',
'              and data_type = c2.c002;',
'          if l_data_type_count <> 1 then',
'              return false;',
'          end if;',
'     end loop;',
'end loop;',
'end loop;',
'return true;',
'exception when no_data_found then return false;',
'end;'))
,p_validation_type=>'FUNC_BODY_RETURNING_BOOLEAN'
