,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Load New Body'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_type     varchar2(300);',
'  l_params   varchar2(32767);',
'  l_has_args boolean;',
'  l_name     varchar2(2000);',
'  l_owner    varchar2(2000);',
'  l_datatype varchar2(2000);',
'  l_ret      varchar2(32767);',
'  i          number;',
'begin',
'   for r in ',
'      ( select object_name,owner ',
'          from sys.dba_objects ',
'         where object_id = :P186_PACKAGE ) loop',
'      l_name := r.object_name;',
'      l_owner := r.owner;',
'   end loop;',
'',
'   l_ret := l_ret || ''create or replace package body "''||l_name||''" is''||chr(10);',
'',
'   for r in ',
'      ( select distinct object_name,overload ',
'          from sys.dba_ARGUMENTS',
'         where owner = l_owner',
'           and package_name = l_name ) loop',
'',
'      l_params := null;',
'      l_type := ''procedure'';',
'      i := 0;',
'      l_has_args := false;',
'',
'      for r1 in ',
'         ( select * ',
'             from sys.dba_ARGUMENTS',
'            where owner        = l_owner',
'              and package_name = l_name',
'              and ( overload   = r.overload or',
'                    (overload is null and r.overload is null) ) ',
'              and object_name  = r.object_name',
'            order by object_name, argument_name, position ) loop',
'',
'         i := i+1; /* arg count */',
'         l_datatype := case when r1.type_name is not null then',
'                            case when r1.type_owner <> ''PUBLIC'' then r1.type_owner||''.'' end ||',
'                                      r1.type_name||''.''||r1.type_subname                         ',
'                            else r1.pls_type end;',
'                ',
'        if ( r1.argument_name is null and r1.in_out = ''OUT'' ) then',
'          l_type := ''function'';',
'          l_params := l_params||case when i>1 then '')'' end || '' return ''||l_datatype|| chr(10);',
'        end if;   ',
'        ',
'        if ( i > 1 AND r1.argument_name is not null ) then ',
'          l_params := l_params||''      ,''||r1.argument_name ||'' ''||',
'                      replace(r1.in_out,''/'','' '')||'' ''||l_datatype|| chr(10);',
'          l_has_args:= true;',
'          elsif ( r1.argument_name is not null ) then',
'              l_params := l_params||''       ''||r1.argument_name||'' ''||',
'                          replace(r1.in_out,''/'','' '')||'' ''||l_datatype|| chr(10);',
'              l_has_args := true;',
'        end if;',
'      end loop;/* end argument loop */',
'            ',
'      l_ret := l_ret || l_type||'' ''||r.object_name||',
'               case when (l_has_args and l_type = ''procedure'') or ',
'                         (l_has_args and l_type = ''function'') then ''('' end;',
'      l_ret := l_ret || l_params||case when l_type = ''procedure'' and l_has_args ',
'                                  then '')'' end||chr(10);',
'      l_ret := l_ret || ''as''||chr(10);',
'      l_ret := l_ret || ''begin''||chr(10);',
'      l_ret := l_ret || '' null; /* insert ''||l_type||'' code */''||chr(10);',
'      l_ret := l_ret || ''end ''||r.object_name||'';''||chr(10);',
'      l_ret := l_ret || ''''||chr(10);',
'',
'   end loop;/* end main loop */',
'',
'   l_ret := l_ret || ''end "''||l_name||''";''||chr(10);',
'   l_ret := l_ret || ''/'';',
'',
'   :P187_SQL := l_ret;',
'',
'end;'))
,p_process_when=>'P187_SQL'
,p_process_when_type=>'ITEM_IS_NULL'
);
