,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with my_s as (select sid from sys.gv_$mystat where rownum = 1),',
'system_msg_prv as (',
'    select rownum, wwv_flow_lang.system_message(''SESSION_MON_PREVIOUS'') the_message from dual),',
'system_msg_cur as (',
'    select rownum,wwv_flow_lang.system_message(''SESSION_MON_CURRENT'') the_message from dual)',
' select case when (select sid from my_s where rownum = 1) = sid then  ',
'                  ''C'' ',
'             when status = ''ACTIVE'' then ',
'                  case when last_call_et > 30  ',
'                       then ''L'' ',
'                       else ''A'' ',
'                  end ',
'             when  status = ''INACTIVE'' ',
'                       then ''I'' ',
'            else null ',
'        end session_status, sid,',
'        username,current_previous,inst_id,',
'        case when vs.status = ''ACTIVE'' ',
'                 then last_call_et ',
'             else null end seconds_in_wait,',
'        case when length(sql_text) > decode(:P542_COLUMNS,''LESS'',125,75)',
'             then substr(replace(sql_text,'','','', ''),1,decode(:P542_COLUMNS,''LESS'',125,75))||''...''',
'             else replace(sql_text,'','','', '') end sql_text,',
'             sql_text full_sql,address,hash_value,child_number,',
'             case when exists (select 1 cnt from sys.gv_$sql_plan p ',
'                         where p.address = vs.address ',
'                           and vs.hash_value = p.hash_value',
'                           and vs.child_number = p.child_number',
'                           and vs.inst_id = p.inst_id) ',
'                  then ''<a href="f?p=''||:APP_ID||'':551:''||:APP_SESSION||''::NO::P551_ADDRESS,P551_HASH_VALUE,P551_CHILD_NUMBER,P551_INST_ID:''||',
'                       vs.address||'',''||vs.hash_value||'',''||vs.child_number||'',''||vs.inst_id ||''">''||',
'                            wwv_flow_escape.html(',
'                            case when length(sql_text) > decode(:P542_COLUMNS,''LESS'',125,75)',
'                             then substr(replace(sql_text,'','','', ''),1,decode(:P542_COLUMNS,''LESS'',125,75))||''...''',
'                             else replace(sql_text,'','','', '')',
'                            end) ||',
'                       ''</a>''',
'                else wwv_flow_escape.html(',
'                     case when length(sql_text) > decode(:P542_COLUMNS,''LESS'',125,75)',
'                     then substr(replace(sql_text,'','','', ''),1,decode(:P542_COLUMNS,''LESS'',125,75))||''...''',
'                     else replace(sql_text,'','','', '')',
'                     end )',
'                end link1',
'   from   ',
'   (select s.sid,s.inst_id,s.status,s.last_call_et SECONDS_IN_WAIT,s.username,',
'                (select the_message from system_msg_cur where rownum = 1) current_previous,',
'                sql_text,fetches,executions,parse_calls,disk_reads,cpu_time,elapsed_time,',
'                q.address,q.hash_value,q.child_number,last_call_et',
'           from sys.gv_$session s, sys.gv_$sql q',
'          where s.sql_id is not null',
'            and s.inst_id = q.inst_id',
'            and s.sql_address = q.address',
'            and s.sql_hash_value = q.hash_value',
'            and s.USERNAME is not null',
'            and nvl(s.osuser,''x'') <> ''SYSTEM''',
'            and s.type <> ''BACKGROUND''',
'          union',
'         select s.sid,s.inst_id,s.status,s.last_call_et SECONDS_IN_WAIT,s.username,',
'                (select the_message from system_msg_prv where rownum = 1) current_previous,',
'                sql_text,fetches,executions,parse_calls,disk_reads,cpu_time,elapsed_time,',
'                q.address,q.hash_value,q.child_number,last_call_et',
'           from sys.gv_$session s, sys.gv_$sql q',
'          where s.sql_id is null',
'            and s.inst_id = q.inst_id',
'            and s.prev_sql_addr = q.address',
'            and s.prev_hash_value = q.hash_value',
'            and s.USERNAME is not null',
'            and nvl(s.osuser,''x'') <> ''SYSTEM''',
'            and s.type <> ''BACKGROUND'') vs',
'  where 1=1',
'    and (:P542_STATUS = ''ALL'' or (:P542_STATUS = ''ACTIVE'' and vs.status = ''ACTIVE''))',
'    and (:P542_SEARCH is null',
'        or',
'        (:P542_SEARCH is not null',
'         and',
'            (vs.username like ''%''||upper(:P542_SEARCH)||''%'' ',
'             or ',
'             upper(vs.sql_text) like ''%''||upper(:P542_SEARCH)||''%''',
'             or',
'             to_char(vs.sid) = :P542_SEARCH ',
'            )',
