'  where vs.sid = oc.sid',
'    and vs.inst_id = oc.inst_id',
'    and vs.USERNAME > ''!''',
'    and nvl(vs.osuser,''x'') <> ''SYSTEM''',
'    and vs.type <> ''BACKGROUND''',
'    and (:P543_STATUS = ''ALL'' or (:P543_STATUS = ''ACTIVE'' and vs.status = ''ACTIVE''))',
'    and (:P543_SEARCH is null',
'        or',
'        (:P543_SEARCH is not null',
'         and',
'            (vs.username like ''%''||upper(:P543_SEARCH)||''%'' ',
'             or ',
'             upper(vs.osuser) like ''%''||upper(:P543_SEARCH)||''%'' ',
