'declare',
'  l_sql varchar2(32700);',
'  report wwv_flow_global.vc_arr2;',
'  binds  wwv_flow_global.vc_arr2;',
'',
'  l_csv_charset varchar2(255) := NULL;',
'  l_export_clob clob;',
'  l_export_blob blob;  ',
'        ',
'begin',
'',
'  wwv_flow.g_browser_language := :P1204_BROWSER_LANG;',
'  wwv_flow_lang.alter_session(wwv_flow_lang.map_language(wwv_flow.g_browser_language)); ',
'             ',
'  wwv_flow_init_htp_buffer;',
'  wwv_flow.g_page_text_generated := true;',
'  wwv_flow.g_unrecoverable_error := true;',
'    ',
'  wwv_flow_utilities.print_download_header(',
'    p_mime_type =>     ''text/plain'',',
'    p_file_name =>     ''report.csv'',',
'    p_is_attachment => true',
'  );',
'  ',
'  sys.owa_util.http_header_close;',
'',
'  if wwv_flow_preferences.get_preference( p_preference => ''F4500_CSV_DOWNLOAD_UNICODE'' ) = ''Y'' then',
'      l_csv_charset := ''AL32UTF8'';',
'  else',
'      for c1 in (',
'          select csv_encoding, flow_language_derived_from',
'          from wwv_flows',
'          where id = wwv_flow.g_flow_id',
'      ) loop',
'          if c1.flow_language_derived_from <> ''0'' and nvl(c1.csv_encoding,''N'') = ''Y'' then',
'              l_csv_charset := wwv_flow_lang.get_nls_windows_charset;',
'          end if;',
'          exit;',
'      end loop;',
'  end if;',
'  ',
'  if (l_csv_charset is not null) then  ',
'     sys.dbms_lob.createtemporary( l_export_clob, true, sys.dbms_lob.session );    ',
'     sys.dbms_lob.open( l_export_clob, sys.dbms_lob.lob_readwrite );    ',
'  end if;  ',
'    ',
'-- get the statement to run',
'  l_sql := v(''P1003_SQL_COMMAND2'');',
'',
'-- get the binds values used last time if they exist',
'  binds := wwv_flow_utilities.string_to_table (',
'                            str => v(''P1200_BIND_VALS''),',
'                            sep => '','');',
'',
'  for i in 1..binds.count loop',
'   if binds(i) = ''HTMLDB:NULL'' then',
'    binds(i) := null;',
'   end if;',
'  end loop;',
'',
'  report := wwv_flow_render_query.get_report (',
'        p_owner  => v(''P1003_SCHEMA''),',
'        p_query  => l_sql,',
'        p_values => binds,',
'        p_format => ''CSV'',',
'        p_limit_type => ''R'',',
'        p_max_rows => v(''P1003_ROWS'')',
'    );',
'',
'  -- print the results',
'  for i in 1..report.count loop',
'  ',
'     if (l_csv_charset is not null) then',
'        sys.dbms_lob.writeappend( l_export_clob, length(report(i)||chr(10)), report(i)||chr(10));    ',
'     else',
'        sys.htp.p(report(i));',
'     end if;',
'',
'  end loop;',
'',
'  if (l_csv_charset is not null) then',
'    ',
'        sys.dbms_lob.close( l_export_clob );',
'        ',
'        l_export_blob := wwv_flow_utilities.clob_to_blob( p_clob => l_export_clob, p_charset => l_csv_charset );',
'        ',
'        -- download the binary contents of the BLOB',
'        wpg_docload.download_file( l_export_blob ); ',
'    ',
'        -- cleanup the temporary BLOB ',
'        sys.dbms_lob.freetemporary( l_export_blob );   ',
'',
'        -- cleanup the temporary CLOB     ',
'        sys.dbms_lob.freetemporary( l_export_clob );',
'        ',
'  end if;   ',
'      ',
'  wwv_flow.g_unrecoverable_error := true;',
'',
'end;'))
,p_process_error_message=>'error'
,p_process_success_message=>'correcto'
);
