'select c.owner,',
'       c.constraint_name,',
'       decode(c.constraint_type,',
'              ''P'',wwv_flow_lang.system_message(''F4500_PRIMARY''),',
'              ''U'',wwv_flow_lang.system_message(''F4500_UNIQUE''),',
