,p_process_name=>'send email'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   l_report varchar2(32767);',
'   l_body   varchar2(32767);',
'   l_from   varchar2(255) := null;',
'   l_crlf constant varchar2(4) := unistr(''\000d'') || unistr(''\000a'');',
'begin',
'   -- determine from',
'   for c1 in (',
'       select email_address ',
'       from wwv_flow_fnd_user ',
'       where security_group_id = :flow_security_group_id and ',
'           user_name = :app_user) loop',
'           l_from := c1.email_address;',
'   end loop;',
'',
'   if l_from is null or instr(l_from,''@'') = 0 or instr(l_from,''.'') = 0 then',
'     l_from := :P76_TO;',
'   end if;',
'',
'   -- run report',
'   wwv_flow_workspace_reports.report_workspace_usage (',
'    p_security_group_id => :P76_WORKSPACE_ID,',
'    p_time_frame_DWMY => :P76_REPORTING_PERIOD,',
'    p_print_report_yn => ''N'',',
'    p_email_format_yn => ''Y'',',
'    p_date_format => :DATE_TIME_FORMAT);',
