'declare ',
'    l_found boolean := false;',
'    l_first_schema wwv_flow_companies.first_schema_provisioned%type := upper(:P277_SCHEMA);',
'begin',
'    if wwv_flow_provisioning.site_admin_is_restricted and ',
'      wwv_flow_provisioning.restricted_schema(p_schema => l_first_schema,',
'          p_workspace_name => wwv_flow_security.find_company_name(:P277_IMP_COMPANY_ID)) then',
'      l_first_schema := ''INVALID'';',
'    end if;',
'    update wwv_flow_companies',
'      set FIRST_SCHEMA_PROVISIONED = l_first_schema',
'      where PROVISIONING_COMPANY_ID = :P277_IMP_COMPANY_ID;',
'    for c1 in ',
'      (select schema from wwv_flow_company_schemas where security_group_id = :P277_IMP_COMPANY_ID ',
'       and schema = upper(:P277_SCHEMA)) loop',
'       l_found := true;',
'    end loop;',
'    if not l_found then',
'      insert into wwv_flow_company_schemas (security_group_id,schema)  ',
'        values(:P277_IMP_COMPANY_ID,:P277_SCHEMA);',
'    end if;',
'    if wwv_flow_provisioning.site_admin_is_restricted then',
'        for c1 in ',
'          (select id from wwv_flow_company_schemas ',
'              where security_group_id = :P277_IMP_COMPANY_ID and',
'              wwv_flow_provisioning.restricted_schema_i(schema,',
'                wwv_flow_security.find_company_name(:P277_IMP_COMPANY_ID)) = 1) loop',
'           delete wwv_flow_company_schemas where id = c1.id;           ',
'        end loop;',
'    end if;    ',
'    for c1 in ',
'      (select id from wwv_flow_company_schemas ',
'          where security_group_id = :P277_IMP_COMPANY_ID and',
'          not exists (select 1 from sys.dba_users where username = schema)) loop',
'       delete wwv_flow_company_schemas where id = c1.id;           ',
'    end loop;        ',
'end;'))
,p_process_error_message=>unistr('Erreur lors de la d\00E9finition de sch\00E9mas avec infos de param\00E9trage fournies.')
