);
wwv_flow_api.create_message(
 p_id=>wwv_flow_api.id(275988998143166441)
,p_name=>'PA.APP.DESC.7830'
,p_message_language=>'fr'
,p_message_text=>unistr('<p>Cette application pr\00E9sente les fonctions de g\00E9n\00E9ration de graphiques d''Oracle Application Express. Elle comprend des graphiques Flash, int\00E9gralement d\00E9claratifs et pouvant facilement \00EAtre cr\00E9\00E9s et modifi\00E9s via les assistants natifs d''Application E')
