':P7_NEXT_MILESTONE_ID     := null;',
':P7_PREVIOUS_MILESTONE_ID := null;',
'',
'for c1 in ( select event_name',
'              from wwv_flow_events ',
'             where id = :P7_ID',
'               and security_group_id = :flow_security_group_id) loop',
'    :P7_MILESTONE_NAME := c1.event_name;',
