'with dts as (',
'    select trunc(sysdate-(level-1)) the_date',
'    from dual',
'    connect by level < 21',
'), bugs_o as (',
'    select trunc(created_on) created_on,',
'        count(*) the_count',
'    from wwv_flow_bugs',
'    where security_group_id = :FLOW_SECURITY_GROUP_ID',
'        and created_on > add_months( trunc(sysdate), -1 )',
'        and (nvl(:P3506_RELEASE,''0'') = ''0''',
'            or fix_by_release = :P3506_RELEASE)',
'    group by trunc(created_on)',
'), bugs_c as (',
'    select trunc(actual_fix_date) actual_fix_date,',
'        count(*) the_count',
'    from wwv_flow_bugs',
'    where security_group_id = :FLOW_SECURITY_GROUP_ID',
'        and actual_fix_date is not null',
'        and actual_fix_date > add_months( trunc(sysdate), -1 )',
'        and (nvl(:P3506_RELEASE,''0'') = ''0''',
'            or fix_by_release = :P3506_RELEASE)',
'    group by trunc(actual_fix_date)',
')',
'select to_char(dts.the_date,''&DATE_FORMAT.'') label,',
'    nvl(bugs_o.the_count,0) open_value,',
'    wwv_flow_lang.system_message(''OPEN'') series,',
'    dts.the_date,',
'    dts.the_date||'' (''||wwv_flow_lang.system_message(''OPEN'')||'')''||'' ''||nvl(bugs_o.the_count,0) "TOOLTIP"',
'from bugs_o, dts',
'where bugs_o.created_on(+) = dts.the_date',
'union all',
'select to_char(dts.the_date,''&DATE_FORMAT.'') label,',
'    nvl(bugs_c.the_count,0) close_value,',
'    wwv_flow_lang.system_message(''CLOSED'') series,',
'    dts.the_date,',
'    dts.the_date||'' (''||wwv_flow_lang.system_message(''CLOSED'')||'')''||'' ''||nvl(bugs_c.the_count,0) "TOOLTIP"',
'from bugs_c, dts',
'where bugs_c.actual_fix_date(+) = dts.the_date',
'order by 4,3'))
