,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_session varchar2(255) := :APP_SESSION;',
'    l_security_group_id number := :FLOW_SECURITY_GROUP_ID;',
'    l_days    varchar2(255) := null;',
'    l_release varchar2(255) := :P4000_RELEASE;',
'begin',
'    -- milestones',
'    for c1 in ( select /* APEX4d004e42 */ count(*) milestones,',
'                    max(EVENT_DATE) max_date,',
'                    sum(decode(',
'                        greatest(trunc(sysdate),trunc(event_date)),',
'                        trunc(sysdate),0,1)) future_events',
'                from wwv_flow_events e',
'                where security_group_id = l_security_group_id',
'                    and (nvl(l_release,''0'') = ''0''',
'                        or release = l_release)',
'            ) loop',
'        if c1.milestones = 0  then',
'            sys.htp.p(''<div class="a-BadgeChart a-BadgeChart--justGageLike">'');',
'            sys.htp.p(''  <a href="f?p=4800:6006:''||l_session||''" title="View Milestones Dashboard" class="a-BadgeChart-link">'');',
'            sys.htp.p(''    <span class="a-BadgeChart-value">0</span> '');',
'            sys.htp.p(''  </a>'');',
'            sys.htp.p(''  <div class="a-BadgeChart-text">'');',
'            sys.htp.p(''    <span class="a-BadgeChart-label">0 ''||wwv_flow_lang.system_message(''MILESTONES'')||''</span>'');',
'            sys.htp.p(''    <span class="a-BadgeChart-desc">'');',
'            sys.htp.p(''    </span>'');',
'            sys.htp.p(''  </div>'');',
'            sys.htp.p(''</div>'');',
'        else',
'            l_days :=  trim(to_char((trunc(c1.max_date) - trunc(sysdate)),''999G999''));',
'            sys.htp.p(''<div class="a-BadgeChart a-BadgeChart--justGageLike">'');',
'            sys.htp.p(''  <a href="f?p=4800:6000:''||l_session||''::NO:RP,6000,RIR:IREQ_DAYS_OUT:''||l_days||'':" title="View Milestones Report" class="a-BadgeChart-link">'');',
'            sys.htp.p(''    <span class="a-BadgeChart-value">''||l_days||''</span> '');',
'            sys.htp.p(''  </a>'');',
'            sys.htp.p(''  <div class="a-BadgeChart-text">'');',
'            sys.htp.p(''    <span class="a-BadgeChart-label">''||wwv_flow_lang.system_message(''DAYS'')||''</span>'');',
'            sys.htp.p(''    <span class="a-BadgeChart-desc">'');',
'            if nvl(l_release,''0'') = ''0'' then',
'                sys.htp.prn(wwv_flow_lang.system_message(''DAYS_BEFORE_FINAL_MILESTONE''));',
'            else',
'                sys.htp.prn(wwv_flow_lang.system_message(''DAYS_BEFORE_FINAL_MILESTONE_OF_RELASE'',wwv_flow_escape.html(l_release)));',
'            end if;',
'            sys.htp.p(''    </span>'');',
'            sys.htp.p(''  </div>'');',
'            sys.htp.p(''</div>'');',
'        end if;',
'    end loop;',
'end;'))
,p_plug_source_type=>'NATIVE_PLSQL'
,p_plug_query_headings_type=>'QUERY_COLUMNS'
,p_plug_query_num_rows=>15
,p_plug_query_num_rows_type=>'NEXT_PREVIOUS_LINKS'
