'            p_c002            => ''APPLICATION_ITEM'',',
'            p_c003            => :P350_NAME ,',
'            p_c004            => :P350_DESCRIPTION,',
'            p_c005            => :P350_DATA_TYPE,',
'            p_c006            => :P350_PROTECTION_LEVEL,',
