'declare',
'    l_cnt  pls_integer := 0;',
'begin',
'    for l_page in (select c001 page_name,',
'                          c002 page_type,',
'                          c003 source_type,',
'                          c005 table_name,',
'                          c013 page_icon,',
'                          C011 ig_is_editable,',
'                          c012 include_form,',
'                          c015 is_admin_page,',
'                          c040 help,',
'                          c041 link_column,',
'                          c042 link_key,',
'                          c043 link_to_page,',
'                          c044 link_target_key,',
'                          clob001 source,',
'                          n002    internal_page_id',
'                   from apex_collections',
'                   where collection_name = ''APEX$CREATE_APP_PAGE''',
'                   and seq_id = :P1020_SEQ_ID )',
'    loop',
'        :P1020_PAGE_NAME := l_page.page_name;',
'        :P1020_PAGE_TYPE := l_page.page_type;',
'        :P1020_REPORT_TYPE := l_page.page_type;',
'        :P1020_PAGE_ICON := l_page.page_icon;',
'        :P1020_PAGE_SOURCE := l_page.source_type;',
'        if l_page.source_type = ''TABLE'' then',
'            :P1020_TABLE := l_page.table_name;',
'            :P1020_INCLUDE_FORM := l_page.include_form;',
'        else',
'            :P1020_SQL := l_page.source;',
'        end if;',
'        :P1020_ADMIN := l_page.is_admin_page;',
'        -- drill down',
'        :P1020_LINK_COLUMN     := l_page.link_column;',
'        :P1020_LINK_KEY        := l_page.link_key;',
'        :P1020_LINK_TO_PAGE    := l_page.link_to_page;',
'        :P1020_LINK_TARGET_KEY := l_page.link_target_key;',
'        :P1020_PAGE_HELP       := l_page.help;',
'        ',
'        -- set object type (used for Include Form item)',
'        for l_obj in (',
'            select object_type ',
'            from sys.dba_objects ',
'            where owner = :P1_SCHEMA ',
'            and object_name = l_page.table_name )',
'        loop',
'            :P1020_OBJ_TYPE := l_obj.object_type;',
'        end loop;',
'    ',
'        for l_fk in (',
'            select c003            fk_column,',
'                   c005||''.''||C007 display_column',
'            from wwv_flow_collections',
'            where collection_name = ''APEX$CREATE_APP_PAGE_FK''',
'            and n002 = l_page.internal_page_id',
'            order by seq_id )',
'        loop',
