'            if l_cnt = 1 then',
'                :P1020_FK1_COL := l_fk.fk_column;',
'                :P1020_FK1_DISPLAY := l_fk.display_column;',
'            elsif l_cnt = 2 then',
'                :P1020_FK2_COL := l_fk.fk_column;',
'                :P1020_FK2_DISPLAY := l_fk.display_column;',
'            elsif l_cnt = 3 then',
'                :P1020_FK3_COL := l_fk.fk_column;',
'                :P1020_FK3_DISPLAY := l_fk.display_column;',
'            elsif l_cnt = 4 then',
