'     l_from := :P76_TO;',
'   end if;',
'',
'   -- run report',
'   wwv_flow_workspace_reports.report_workspace_usage (',
'    p_security_group_id => :P76_WORKSPACE_ID,',
'    p_time_frame_DWMY => :P76_REPORTING_PERIOD,',
'    p_print_report_yn => ''N'',',
'    p_email_format_yn => ''Y'',',
'    p_date_format => :DATE_TIME_FORMAT);',
'    ',
'   -- get output',
'   l_report := wwv_flow_workspace_reports.get_workspace_usage_output;',
'   l_body := :P76_BODY||l_crlf;',
'',
'   -- send email;',
'   APEX_MAIL.SEND(',
'     p_to          => :P76_TO,',
'     p_from        => l_from,',
'     p_body        => l_body,',
'     p_body_html   => l_report,',
'     p_subj        => :P76_SUBJECT,',
'     p_cc          => null,',
'     p_bcc         => null,',
'     p_replyto     => null);',
'',
'   -- push',
'   APEX_MAIL.PUSH_QUEUE;',
'end;'))
,p_process_success_message=>'Posta elettronica inviata'
);
