,p_process_name=>'Create application meta data'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_app_type varchar2(30);',
'    l_apex_application_id number;',
'    l_apex_websheet_id number;',
'    l_sgid             number;',
'begin',
'',
'    if :P112_FILE_TYPE = ''FLOW_EXPORT'' then',
'        l_app_type := ''DB'';',
'        l_apex_application_id := :P109_NEW_APPLICATION_ID;',
'        l_apex_websheet_id := null;',
'    else',
'        l_app_type := ''WS'';',
'        l_apex_websheet_id := :P109_NEW_APPLICATION_ID;',
'        l_apex_application_id := null;',
'    end if;',
'    ',
'    if nvl(:P109_APP_GROUP,''CUSTOM'') = ''CUSTOM'' then',
'        l_sgid := 11;',
'    else',
'        l_sgid := 12;',
'    end if;',
'    ',
'    insert into wwv_flow_pkg_applications (',
'        app_id,',
'        app_name,',
'        app_description,',
'        app_type,',
'        app_group,',
'        apex_application_id,',
'        apex_websheet_id,',
'        app_status,',
'        build_version,',
'        app_category_id_1,',
'        unlock_allowed,',
'        security_group_id)',
'    values (',
'        :P109_NEW_APPLICATION_ID,',
'        :P109_NEW_APPLICATION_NAME,',
'        :P109_APP_DESCRIPTION,',
'        l_app_type,',
'        nvl(:P109_APP_GROUP,''CUSTOM''),',
'        l_apex_application_id,',
'        l_apex_websheet_id,',
'        ''AVAILABLE'',',
'        1,',
'        :P109_CATEGORY,',
'        ''Y'',',
'        l_sgid);',
'        ',
'    -- save file',
'    for c1 in (select * ',
'               from wwv_flow_file_objects$',
'               where name = :P112_FILE',
'               and id = :P109_FILE_ID)',
'    loop',
'        insert into wwv_flow_pkg_app_files (',
'            app_id, ',
'            language,',
'            file_name, ',
'            mime_type,',
'            file_charset,',
'            blob_content,',
'            security_group_id)',
'        values (',
'            :P109_NEW_APPLICATION_ID,',
'            c1.language,',
'            c1.title,',
'            c1.mime_type,',
'            c1.file_charset,',
'            c1.blob_content,',
'            l_sgid);',
'    end loop;',
'    ',
'    -- Clean up wwv_flow_file_objects$ table',
'    delete from wwv_flow_file_objects$',
'    where name = :P112_FILE',
'    and id = :P109_FILE_ID;',
'end;'))
