,p_process_name=>'create collection'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'   l_count     integer;',
'   l_workspace varchar2(255);',
'   l_username  varchar2(255);',
'   l_domain    varchar2(255);',
'   l_email     varchar2(255);',
'   l_schema    varchar2(255);',
'   c_collection_name constant varchar2(255) := ''BULK_USER_CREATE'';',
'   x           integer;',
'   function workspace_exists (p_workspace_name in varchar2) return boolean',
'   is',
'       l_found boolean := false;',
'   begin',
'       for c1 in (select 1 x from wwv_flow_companies where short_name = upper(p_workspace_name)) loop',
'          l_found := true;',
'       end loop;',
'       return l_found;',
'   end;',
'   function schema_exists (p_schema_name in varchar2) return boolean',
'   is',
'       l_found boolean := false;',
'   begin',
'       for c1 in (select 1 x from sys.dba_users where username = upper(p_schema_name)) loop',
'          l_found := true;',
'       end loop;',
'       return l_found;',
'   end;',
'   function workspace_in_collection (p_workspace_name in varchar2) return boolean',
'   is',
'       l_found boolean := false;',
'   begin',
'       for c1 in (select 1 x ',
'                  from   wwv_flow_collections ',
'                  where  collection_name = c_collection_name and ',
'                         upper(c003) = upper(p_workspace_name)  ) loop',
'          l_found := true;',
'       end loop;',
'       return l_found;',
'   end;',
'begin',
'    if :P115_WORKSPACE_NAMES = ''DOMAIN'' then',
'        wwv_flow_fnd_user_api.create_user_coll_from_csv (',
'            p_users => :P115_EMAIL_ADDRESSES,',
'            p_security_group_id => :flow_security_group_id,',
'            p_strip_domain => ''N'' );',
'        commit;',
'        for c1 in (',
'             select seq_id , c001, c002',
'             from wwv_flow_collections ',
'             where collection_name = c_collection_name order by 1) loop',
'             --',
'             l_email     := lower(c1.c002);',
'             --l_username  := upper(apex_util.compress_int(wwv_flow_id.next_val));',
'             l_username  := l_email;',
'             l_workspace := substr(c1.c001,instr(c1.c001,''@'')+1);',
'             l_workspace := substr(l_workspace,1,instr(l_workspace,''.'')-1);',
'             l_workspace := replace(trim(l_workspace),'' '',''_'');',
'             l_domain    := l_workspace;',
'             x := 0;',
'             for j in 1..999 loop',
'                 if not workspace_exists(p_workspace_name=>l_workspace) and',
'                    not workspace_in_collection(p_workspace_name=>l_workspace) and',
'                    not schema_exists(p_schema_name=>l_workspace',
'                    ) then',
'                    exit;',
'                 end if;',
'                 x := x + 1;',
'                 l_workspace := l_domain||''_''||lpad(x,3,''000'');',
'             end loop;',
'             --',
'             --l_username := l_workspace;',
'             l_schema := l_workspace;',
'             ',
'             wwv_flow_collection.update_member(',
'                p_collection_name => c_collection_name,',
'                p_seq => c1.seq_id,',
'                p_c001 => l_username,',
'                p_c002 => l_email,',
'                p_c003 => l_workspace,',
'                p_c004 => :P115_INSTALL_SAMPLE_APP,',
'                p_c005 => :P115_INSTALL_SAMPLE_WS,',
'                p_c006 => :P115_INSTALL_SAMPLE_TABLES,',
'                p_c007 => :P115_QUOTA,',
'                p_c009 => :P115_RM_CONSUMER_GROUP,',
'                p_c010 => :P115_SEND_EMAIL,',
'                p_c011 => :P115_AUTO_PURGE,',
'                p_c012 => :P115_COMMENTS,',
'                p_c013 => l_schema',
'                );',
'        end loop;',
'        commit;',
'    elsif :P115_WORKSPACE_NAMES = ''GENERATED'' then',
'         l_count := :P115_WORKSPACE_COUNT;',
'         wwv_flow_collection.create_or_truncate_collection(',
'            p_collection_name => c_collection_name);',
'         x := 0;',
'         for i in 1..l_count loop',
'             x := x + 1;',
'             l_email := '''';',
'             for j in 0..100 loop',
'                  x := x + j;',
'                  l_workspace := upper(apex_util.compress_int(wwv_flow_id.next_val));',
'                  l_username  := lower(l_workspace);',
'                  if not workspace_exists(p_workspace_name=>l_workspace) and',
'                     not schema_exists(p_schema_name=>l_workspace) ',
'                     then',
'                     exit;',
'                  end if;',
'             end loop;',
'             l_schema := l_workspace;',
'             --',
'             wwv_flow_collection.add_member (',
'                p_collection_name => c_collection_name,',
'                p_c001 => l_username,',
'                p_c002 => l_email,',
'                p_c003 => l_workspace,',
'                p_c004 => :P115_INSTALL_SAMPLE_APP,',
'                p_c005 => :P115_INSTALL_SAMPLE_WS,',
'                p_c006 => :P115_INSTALL_SAMPLE_TABLES,',
'                p_c007 => :P115_QUOTA,',
'                p_c009 => :P115_RM_CONSUMER_GROUP,',
'                p_c010 => :P115_SEND_EMAIL,',
'                p_c011 => :P115_AUTO_PURGE,',
'                p_c012 => :P115_COMMENTS,',
'                p_c013 => l_schema',
'                );',
'         end loop;',
'         commit;',
'    elsif :P115_WORKSPACE_NAMES = ''PREFIX'' then',
'         l_count := :P115_WORKSPACE_COUNT;',
'         wwv_flow_collection.create_or_truncate_collection(',
'            p_collection_name => c_collection_name);',
'         x := 0;',
'         for i in 1..l_count loop',
'             x := x + 1;',
'             for j in 0..100 loop',
'                  x := x + j;',
'                  l_workspace := upper(:P115_WORKSPACE_PREFIX)||''_''||lpad(x,3,''000'');',
'                  l_workspace := replace(trim(l_workspace),'' '',''_'');',
'                  if not workspace_exists(p_workspace_name=>l_workspace) and',
'                     not schema_exists(p_schema_name=>l_workspace) ',
'                     then',
'                     exit;',
'                  end if;',
'             end loop;',
'             l_email     := '''';',
'             l_username  := lower(l_workspace);',
'             l_schema := l_workspace;',
'             --',
'             wwv_flow_collection.add_member (',
'                p_collection_name => c_collection_name,',
'                p_c001 => l_username,',
'                p_c002 => l_email,',
'                p_c003 => l_workspace,',
'                p_c004 => :P115_INSTALL_SAMPLE_APP,',
'                p_c005 => :P115_INSTALL_SAMPLE_WS,',
'                p_c006 => :P115_INSTALL_SAMPLE_TABLES,',
'                p_c007 => :P115_QUOTA,',
'                p_c009 => :P115_RM_CONSUMER_GROUP,',
'                p_c010 => :P115_SEND_EMAIL,',
'                p_c011 => :P115_AUTO_PURGE,',
'                p_c012 => :P115_COMMENTS,',
'                p_c013 => l_schema',
'                );',
'         end loop;',
'         commit;',
'    end if;',
'end;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
);
end;
