,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    x   varchar2(32767) := null;',
'begin',
'    x:= ''',
'    select c.provisioning_company_id security_group_id,',
'           c.short_name,count(s.schema) schemas,',
'           -- apps',
'           max(f.apps) apps,max(f.pages) pages,',
'           max(f.last_updated_on) last_updated_on,',
'           --mail',
'           max(nvl(m.mail_sent,0)) mail_sent,',
'           max(nvl(mq.mail_count,0)) mail_in_queue,',
'           -- page views',
'           max(nvl(pv.page_views,0)) page_views,',
'           max(nvl(pv.end_users,0)) end_users,',
'           max(nvl(pv.total_elap,0)) total_elap,',
'           max(nvl(pv.avg_page_view_time,0)) avg_page_view_time,',
'           max(nvl(dv.d_page_views,0)) d_page_views,',
'           max(nvl(dv.act_developers,0)) act_developers       ',
'      from wwv_flow_companies c, wwv_flow_company_schemas s,',
'           -- flows and pages',
'           (select f1.security_group_id,',
'                   count(distinct f1.id) apps,',
'                   count(p1.id) pages,',
'                   max(f1.last_updated_on) last_updated_on',
'              from wwv_flow_steps p1, wwv_flows f1',
'             where f1.security_group_id = p1.security_group_id',
'               and p1.flow_id = f1.id',
'             group by f1.security_group_id) f,',
'           -- email',
'           (select security_group_id,count(*) mail_sent',
'              from wwv_flow_mail_log',
'             group by security_group_id) m,',
'           -- email queue',
'           (select security_group_id,count(*) mail_count',
'              from wwv_flow_mail_queue',
'             where last_updated_on < sysdate - 1',
'             group by security_group_id) mq,',
'             -- app page views',
'           (select security_group_id,count(*) page_views,count(distinct userid) end_users,',
'                   sum(elap) total_elap,avg(ELAP) avg_page_view_time',
'                      from wwv_flow_activity_log',
'                     where (flow_id > 4999 or flow_id < 4000)',
'                     group by security_group_id) pv,',
'             -- developer page views',
'           (select security_group_id,count(*) d_page_views,count(distinct userid) act_developers',
'                      from wwv_flow_activity_log',
'                     where flow_id between 4000 and 4999',
'                     group by security_group_id) dv',
'     where s.security_group_id = c.provisioning_company_id '';',
'     if :P501_SEARCH is not null then',
'        x:=x||'' and upper(c.short_name) like ''''%''''||upper(:P501_SEARCH)||''''%'''' '';',
'     end if;',
'     x:=x||''',
'       and c.provisioning_company_id = :P501_SGID',
'       and c.provisioning_company_id = f.security_group_id',
'       and c.provisioning_company_id = m.security_group_id(+)',
'       and c.provisioning_company_id = mq.security_group_id(+)',
'       and c.provisioning_company_id = pv.security_group_id(+)',
'       and c.provisioning_company_id = dv.security_group_id(+)',
'     group by c.provisioning_company_id,c.short_name '';',
'',
'    return x;',
'end;',
'     '))
