'',
':P56_SHOW_EXPIRE_FLAG := ''N'';',
'if :P56_DEVELOPER = ''Y'' then',
'  if l_account_last_updated + l_site_account_lifetime < sysdate then',
'    l_dev_admin_expired := true;',
'    :P56_EXPIRATION_STATUS1 := wwv_flow_lang.system_message(''EXPIRED'');',
'  else',
'    :P56_EXPIRATION_STATUS1 := wwv_flow_lang.system_message(''VALID'');',
'    :P56_SHOW_EXPIRE_FLAG := ''Y'';',
'  end if;',
'else',
'  :P56_EXPIRATION_STATUS1 := ''NA'';',
'end if;',
'',
'for c1 in (select ACCOUNT_LIFETIME_DAYS from WWV_FLOW_COMPANIES',
'           where (',
'             EXPIRE_FND_USER_ACCOUNTS = ''Y'' or ',
'             wwv_flow_platform.get_preference(''EXPIRE_FND_USER_ACCOUNTS'') = ''Y'')',
'           and provisioning_company_id = :flow_security_group_id) loop',
'  l_found := true;',
'  if l_account_last_updated + l_workspace_account_lifetime < sysdate then',
'    l_end_user_expired := true;',
'    :P56_EXPIRATION_STATUS2 := wwv_flow_lang.system_message(''EXPIRED'');',
'  else',
'    :P56_EXPIRATION_STATUS2 := wwv_flow_lang.system_message(''VALID'');',
'    :P56_SHOW_EXPIRE_FLAG := ''Y'';',
'  end if;',
'end loop;',
'if not l_found then',
'  :P56_EXPIRATION_STATUS2 := ''NA'';',
'end if;',
'end;'))
);
end;
