,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare ',
'  l_p1 varchar2(256) := v(''F4350_P56_WEB_PASSWORD'');',
'  l_p2 varchar2(256) := v(''F4350_P56_USER_PASSWORD'');',
'  l_msg varchar2(4000);',
'  l_body_html varchar2(32767) := null;',
'  l_product_name      varchar2(500);',
'  l_developer_roles varchar2(255) := null;',
'  l_ALLOW_APP_BUILDING_YN     varchar2(1);',
'  l_ALLOW_SQL_WORKSHOP_YN  varchar2(1);',
'  l_ALLOW_WEBSHEET_DEV_YN   varchar2(1);',
'  l_ALLOW_TEAM_DEVELOPMENT_YN varchar2(1);',
'begin',
'    if :F4350_P56_DEFAULT_SCHEMA is not null then',
'        wwv_flow_sw_api.check_priv(p_schema=>:F4350_P56_DEFAULT_SCHEMA);',
'    end if;',
'',
'    l_ALLOW_APP_BUILDING_YN     := nvl(v(''P56_ALLOW_APP_BUILDING_YN''),''Y'');',
'    l_ALLOW_SQL_WORKSHOP_YN     := nvl(v(''P56_ALLOW_SQL_WORKSHOP_YN''),''Y'');',
'    l_ALLOW_WEBSHEET_DEV_YN     := nvl(v(''P56_ALLOW_WEBSHEET_DEV_YN''),''Y'');',
'    l_ALLOW_TEAM_DEVELOPMENT_YN := nvl(v(''P56_ALLOW_TEAM_DEVELOPMENT_YN''),''Y'');',
'    ',
'    wwv_flow.update_cache_with_write(''F4350_P56_WEB_PASSWORD'',null);',
'    wwv_flow.update_cache_with_write(''F4350_P56_USER_PASSWORD'',null);',
'    ',
'    wwv_flow_fnd_developer_api.edit_fnd_user (',
'     p_user_id                      => :F4350_P56_USER_ID,           ',
'     p_user_name                    => :F4350_P56_USER_NAME,         ',
'     p_first_name                   => :F4350_P56_FIRST_NAME,          ',
'     p_last_name                    => :F4350_P56_LAST_NAME,',
'     p_web_password                 => l_p1,    ',
'     p_new_password                 => l_p2,                     ',
'     p_email_address                => :F4350_P56_EMAIL_ADDRESS,       ',
'     p_allow_access_to_schemas      => :F4350_P56_ACCESS_TO_SCHEMAS,   ',
'     p_default_schema               => :F4350_P56_DEFAULT_SCHEMA, ',
'     p_default_date_format          => :P56_DEFAULT_DATE_FORMAT,',
'     p_group_ids                    => :F4350_P56_GROUPS,',
'     P_DESCRIPTION                  => :P56_DESCRIPTION,',
'     --',
'     P_ACCOUNT_EXPIRY               => :P56_ACCOUNT_EXPIRY,',
'     P_ACCOUNT_LOCKED               => :P56_ACCOUNT_LOCKED,',
'     p_change_password_on_first_use => :P56_CHANGE_PWD_ON_FIRST_USE,',
'     p_first_password_use_occurred  => case when l_p1 is not null then ''N'' else :P56_FIRST_PWD_USE_OCCURRED end,',
'     --',
'     p_ALLOW_APP_BUILDING_YN        => l_ALLOW_APP_BUILDING_YN,',
'     p_ALLOW_SQL_WORKSHOP_YN        => l_ALLOW_SQL_WORKSHOP_YN,',
'     p_ALLOW_WEBSHEET_DEV_YN        => l_ALLOW_WEBSHEET_DEV_YN,',
'     p_ALLOW_TEAM_DEVELOPMENT_YN    => l_ALLOW_TEAM_DEVELOPMENT_YN',
'    );',
'',
'  --',
'  -- Mail the change of Email address',
'  --',
'  If :F4350_P56_ORG_EMAIL_ADDRESS <> :F4350_P56_EMAIL_ADDRESS then',
'    l_msg       := l_msg || wwv_flow_lang.system_message(''NEW_EMAIL_ADDRESS'') || chr(10) ',
'                      || :F4350_P56_EMAIL_ADDRESS || chr(10) || chr(10) || chr(10);',
'    l_msg       := l_msg || wwv_flow_utilities.host_url(''SCRIPT'') || chr(10);',
'   ',
'    l_body_html := wwv_flow_mail.convert_txt_to_html (',
'                       p_txt_message       => l_msg,',
'                       p_security_group_id => :flow_security_group_id );',
'    l_product_name := nvl(v(''PRODUCT_NAME''),''Oracle Application Express'');',
'',
'    wwv_flow_mail.send ( p_to        => :F4350_P56_ORG_EMAIL_ADDRESS,',
'                         p_from      => nvl(wwv_flow_platform.get_preference(''SMTP_FROM''),:F4350_P56_ORG_EMAIL_ADDRESS),',
'                         p_body      => l_msg,',
'                         p_body_html => l_body_html,',
'                         p_subj      => wwv_flow_lang.system_message(''CHANGE_PW_REQUEST'',l_product_name) );',
'  end if;',
'',
'  --',
'  -- Developer and Admin roles',
'  --',
'  if :P56_ADMIN = ''N'' and :P56_DEVELOPER = ''N'' then',
'    delete from wwv_flow_developers where user_id = :F4350_P56_USER_ID;',
'  else',
'    -- {Trigger on WWV_FLOW_DEVELOPERS sets all developer roles for ADMINS ',
'    --  irrespective of what set here providing includes ''ADMIN''} ',
'    if :P56_ADMIN = ''Y'' then ',
'      l_developer_roles := l_developer_roles||''ADMIN'';',
'    end if;',
'    if nvl(v(''P56_ALLOW_APP_BUILDING_YN''),''Y'') = ''Y'' then ',
'      if l_developer_roles is not null then',
'        l_developer_roles := l_developer_roles||'':'';',
'      end if;',
'      l_developer_roles := l_developer_roles||''CREATE:EDIT:HELP:MONITOR'';',
'    end if;',
'    if nvl(v(''P56_ALLOW_SQL_WORKSHOP_YN''),''Y'') = ''Y'' then',
'      if l_developer_roles is not null then',
'        l_developer_roles := l_developer_roles||'':'';',
'      end if;',
'      l_developer_roles := l_developer_roles||''SQL:MONITOR:DATA_LOADER'';',
'    end if;',
'    wwv_flow_fnd_developer_api.edit_developer_role(',
'             p_id              => 1,',
'             p_user_id         => :F4350_P56_USER_ID,',
'             p_user_name       => :F4350_P56_USER_NAME,',
'             p_developer_roles => l_developer_roles);',
'  end if;',
'',
'  --',
'  -- If the current user is the one being edited in this workspace, then also',
'  -- update the session state value for the user default date format',
'  --',
'  if :APP_USER = upper(:F4350_P56_USER_NAME) then',
'      if trim(:P56_DEFAULT_DATE_FORMAT) is null then',
'          :G_USER_DEFAULT_DATE_FORMAT := :DATE_FORMAT;      ',
'      else',
'          :G_USER_DEFAULT_DATE_FORMAT := :P56_DEFAULT_DATE_FORMAT;',
'      end if;',
'  end if;',
'  ',
'',
'  commit;',
'end;'))
,p_process_error_message=>'Errore durante l''aggiornamento dell''utente.'
,p_process_when_button_id=>wwv_flow_api.id(1939534239325908.4354)
