 p_id=>wwv_flow_api.id(315084600492339087.4404)
,p_process_sequence=>10
,p_process_point=>'AFTER_SUBMIT'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'IRR Update'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'    l_split          number                   := 0;',
'    l_list_vals      varchar2(32767)          := null;',
'    l_vals           varchar2(32767)          := null;',
'    l_arr            wwv_flow_global.vc_arr2;',
'    l_ids            varchar2(32767)          := null;',
'    l_id             number                   := null;',
'    l_count          number;',
'    l_arr_count      number;',
'',
'begin',
'',
'  -- Set Modified TITLE columns',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f04.COUNT',
'    LOOP',
'        ',
'        -- Set TITLE Only For Changed Values',
'        UPDATE WWV_MIG_FRM_REV_LOV',
'           SET title             = wwv_flow.g_f05(i)',
'         WHERE id                = wwv_flow.g_f04(i)',
'           AND security_group_id = :flow_security_group_id',
'           AND nvl2(title,title,'' '') <> nvl(wwv_flow.g_f05(i),'' '')',
'        RETURNING id INTO l_id;',
'         ',
'        if (l_id <> 0) then',
'            if (instr(l_ids,l_id) = 0) or (instr(l_ids,l_id) is null) then',
'                l_ids := l_ids||l_id||'':'';',
'            end if;',
'            l_id := 0;',
'        end if;',
'         ',
'        -- Create string of all IDs for all checkboxes',
'        l_list_vals := l_list_vals||wwv_flow.g_f04(i)||'':'';',
'',
'  END LOOP;',
'',
'  -- Set Selected CheckBoxes to ''Y''',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f01.COUNT',
'    LOOP',
'      l_split := instr(wwv_flow.g_f01(i),'':'');',
'      if (l_split = 0) then',
' ',
'          UPDATE WWV_MIG_FRM_REV_LOV',
'             SET select_for_app    =  ''Y''',
'           WHERE id                =  wwv_flow.g_f01(i)',
'             AND security_group_id =  :flow_security_group_id',
'             AND select_for_app    <> ''Y''',
'          RETURNING id into l_id;',
'',
'          if (l_id <> 0) then',
'              if (instr(l_ids,l_id) = 0) or (instr(l_ids,l_id) is null) then',
'                  l_ids := l_ids||l_id||'':'';',
'              end if;',
'              l_id := 0;',
'          end if;',
'           ',
'          -- Refine List of IDs to Only Include De-Selected checkboxes',
'          l_list_vals := replace(l_list_vals,wwv_flow.g_f01(i)||'':'','''');',
'',
'      end if;',
'  END LOOP;',
'',
'  -- Remove Trailing semi-colon from string of IDs',
'  l_vals := substr(l_list_vals,0,length(l_list_vals) - 1);',
'',
'  -- Convert String of IDs into ARRAY',
'  l_arr := wwv_flow_utilities.string_to_table(l_vals);',
'',
'  l_arr_count := l_arr.count;',
'  ',
'  -- Set De-Selected CheckBoxes to ''N''    ',
'  FORALL i in 1 .. l_arr.count',
'    UPDATE wwv_mig_frm_rev_lov',
'       SET select_for_app = ''N''',
'     WHERE id = l_arr(i)',
'       AND  select_for_app <> ''N'';',
'    -- RETURNING id INTO l_id;',
'         ',
'/*  if (l_id <> 0) then',
'      if (instr(l_ids,l_id) = 0) or (instr(l_ids,l_id) is null) then',
'          l_ids := l_ids||l_id||'':'';',
'      end if;',
'      l_id := 0;',
'  end if; */',
'',
'  -- Remove Trailing semi-colon from string of IDs',
'  l_vals := substr(l_ids,0,length(l_ids) - 1);',
'  -- Convert String of IDs into ARRAY',
'  l_arr := wwv_flow_utilities.string_to_table(l_ids);',
'  l_count := l_arr.count;',
'',
' -- apex_util.set_session_state(''P55_MESSAGE'',l_count||'' Row(s) updated.'');',
'',
'  ',
'end;'))
,p_process_error_message=>'Informazioni della lista di valori non aggiornate.'
,p_process_when_button_id=>wwv_flow_api.id(268959224384734720.4404)
,p_process_success_message=>'Informazioni della lista di valori aggiornate.'
);
