,p_validation_name=>'Checkbox Must Be Visible'
,p_validation_sequence=>30
,p_validation=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  l_worksheet_id number := null;',
'begin',
'',
'  -- Get Worksheet ID',
'  for c1 in (select id ',
'               from wwv_flow_worksheets',
'              where name  = ''List of Values Columns''',
'                and flow_id = 4400',
'  ) loop',
'    l_worksheet_id := c1.id;',
'  end loop;',
'               ',
'  -- Get Visible REPORT_COLUMNS',
'  for c2 in (select report_columns ',
'               from wwv_flow_worksheet_rpts',
'              where worksheet_id = l_worksheet_id',
'                and session_id = :SESSION',
'  ) loop',
'    ------------------------------------------------------------------------',
'    -- Checkbox fields have no associated database column',
'    -- so the wwv_flow_worksheet_rpts.report_columns will only contain',
'    -- an additional space in the report_columns string when the ''Include''',
'    -- checkbox is visible on the IRR. As the user can position the ''Include''',
'    -- column anywhere in the IRR, need to search for the occurrent of an ',
'    -- additional space at the start, middle and end of the report_columns ',
'    -- string.',
'    if (instr(c2.report_columns,'' :'',1,1) = 1) and (instr(c2.report_columns,''TITLE'',1,1) > 0) then',
'      return true;',
'    elsif (instr(c2.report_columns,'': :'',1,1) > 0) and (instr(c2.report_columns,''TITLE'',1,1) > 0) then',
'      return true;',
'    elsif (instr(c2.report_columns,'': '',1,1) > 0) and (instr(c2.report_columns,''TITLE'',1,1) > 0) then   ',
'      return true;',
'    elsif (instr(c2.report_columns,''INCLUDE'') > 0) and (instr(c2.report_columns,''NAME'',1,1) > 0) then',
'      return true;',
'    else',
'      return false;',
'    end if;',
'  end loop;',
'',
'end;'))
,p_validation_type=>'FUNC_BODY_RETURNING_BOOLEAN'
,p_error_message=>'Le colonne Includi e Titolo devono essere visibili nel report al fine di applicare le modifiche alle informazioni sulla lista di valori.'
,p_when_button_pressed=>wwv_flow_api.id(268964729030777852.4404)
