,p_display_point=>'REGION_POSITION_03'
,p_item_display_point=>'BELOW'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select component_count+ ',
'       (select count(*)',
'        from   WWV_MIG_PLSQL_LIBS pl',
'        where  pl.project_id = :MW_PROJ_ID',
'        and    pl.security_group_id = :flow_security_group_id)+',
'       (select count(*)',
'        from   WWV_MIG_RPTS r, WWV_MIG_REPORT rp',
'        where  r.project_id = :MW_PROJ_ID',
'        and    r.security_group_id = :flow_security_group_id',
'        and    r.id = rp.file_id)+',
'       (select count(*)',
'        from   WWV_MIG_FRM_MENUS mu',
'        where  mu.project_id = :MW_PROJ_ID',
'        and    mu.security_group_id = :flow_security_group_id)+',
'       (select count(*)',
'        from   WWV_MIG_OLB o',
'        where  o.project_id = :MW_PROJ_ID',
'        and    o.security_group_id = :flow_security_group_id) component_count, ',
'       completed_components+ ',
'       (select count(*)',
'        from   WWV_MIG_PLSQL_LIBS pl',
'        where  (pl.APPLICABLE = ''N'' or pl.COMPLETE = ''Y'') ',
'        and    pl.project_id = :MW_PROJ_ID',
'        and    pl.security_group_id = :flow_security_group_id)+',
'       (select count(*)',
'        from   WWV_MIG_RPTS r, WWV_MIG_REPORT rp',
'        where  (rp.APPLICABLE = ''N'' or rp.COMPLETE = ''Y'') ',
'        and    r.project_id = :MW_PROJ_ID',
'        and    r.security_group_id = :flow_security_group_id',
'        and    r.id = rp.file_id)+',
'       (select count(*)',
'        from   WWV_MIG_FRM_MENUS mu',
'        where  (mu.APPLICABLE = ''N'' or mu.COMPLETE = ''Y'') ',
'        and    mu.project_id = :MW_PROJ_ID',
'        and    mu.security_group_id = :flow_security_group_id)+',
'       (select count(*)',
'        from   WWV_MIG_OLB o',
'        where  (o.APPLICABLE = ''N'' or o.COMPLETE = ''Y'') ',
'        and    o.project_id = :MW_PROJ_ID',
'        and    o.security_group_id = :flow_security_group_id) completed_components, ',
'       decode(component_count,0,0,100*completed_components / component_count) percent_complete',
'from',
'(',
'select sum(',
'       block_items+',
'       all_triggers+',
'       blocks+',
'       lists_of_values+',
'       alerts+',
'       visual_attributes+',
'       coordinates+',
'       windows+',
'       editors+',
'       canvases+',
'       program_units+',
'       property_classes+',
'       record_groups)',
'       component_count,',
'       --',
'       sum(completed_block_items+',
'       completed_triggers+',
'       completed_blocks+',
'       completed_lists_of_values+',
'       completed_alerts+',
'       completed_visual_attributes+',
'       completed_coordinates+',
'       completed_windows+',
'       completed_editors+',
'       completed_canvases+',
'       completed_program_units+',
'       completed_property_classes+',
'       completed_record_groups)',
'       completed_components',
'from (',
'select ',
'       --',
'       -- components',
'       --',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_BLOCKS t,',
'             WWV_MIG_FRM_BLK_ITEMS i',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id and',
'              t.id = i.block_id) block_items,',
'              (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_TRIGGERS t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) +',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_BLOCKS t,',
'             WWV_MIG_FRM_BLK_TRIGGERS i',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id and',
'              t.id = i.block_id) +',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_BLOCKS t,',
'             WWV_MIG_FRM_BLK_ITEMS i,',
'             WWV_MIG_FRM_BLK_ITEM_TRIGGERS it',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id and',
'              t.id = i.block_id and',
'              i.id = it.item_id) all_triggers,',
'--',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_blocks t,',
'             wwv_mig_frm_rev_blocks rb',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              t.id = rb.block_id and',
'              fm.id = t.formmodule_id) blocks,',
'--',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_LOV t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) lists_of_values,',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_ALERTS t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) alerts,',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_VISUALATTRIBUTES t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) visual_attributes,',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_COORDINATES t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) coordinates,',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_WINDOWS t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
'              fm.id = t.formmodule_id) windows,',
'       (select count(*)',
'        from WWV_MIG_FRM_MODULES m,',
'             WWV_MIG_FRM_FORMMODULES fm,',
'             WWV_MIG_FRM_EDITOR t',
'        where m.file_id = f.id and',
'              m.id = fm.module_id and',
