,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Process Component & Trigger Applicability'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_id           number        := null;',
'  l_component    varchar2(100) := null;',
'BEGIN',
'',
'  -------------------------------------------------------------------',
'  -- Set Trigger-Level Applicability',
'  --',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f04.COUNT',
'    LOOP',
'      update wwv_mig_project_triggers',
'         set form_level = htmldb_application.g_f05(i),',
'             block_level = htmldb_application.g_f06(i),',
'             item_level = htmldb_application.g_f07(i)',
'       where id = htmldb_application.g_f04(i);',
'',
'  END LOOP;',
'',
'',
'  ----------------------------------------------------------------------',
'  -- Set Component Applicability',
'  --',
'  FOR i IN 1 .. HTMLDB_APPLICATION.g_f01.COUNT',
'    LOOP',
'      l_id := 0;',
'     ',
'      -- Update Changed Rows Only to Set Component Applicability',
'      UPDATE    wwv_mig_project_components',
'      SET       applicable = htmldb_application.g_f02(i)',
'      WHERE     id = htmldb_application.g_f01(i)',
'      AND       applicable <> htmldb_application.g_f02(i)',
'      RETURNING id into l_id;',
'',
'      -- Set Applicability for all objects of the selected component type',
'      -- within the migration project',
'      for c1 in (SELECT component ',
'                FROM   wwv_mig_project_components',
'                WHERE  id = l_id',
'                AND    security_group_id = :flow_security_group_id ',
'      ) LOOP      ',
'         l_component := c1.component;',
'',
'         if (l_component is not null) then',
'             wwv_mig_frm_utilities.set_component_applicability(',
'                             p_component_name => l_component,',
'                             p_project_id     => :MW_PROJ_ID,',
'                             p_security_group_id => :flow_security_group_id);',
'         end if;',
'',
'',
'      END LOOP;',
'    ',
'  END LOOP;',
'',
'  ---------------------------------------------------------------------',
'  -- Set Form-Level Triggers across Migration Project',
'  --',
'/*  wwv_mig_frm_utilities.set_formtrig_applicability(',
'                       p_project_id => :MW_PROJ_ID,',
'                       p_security_group_id => :flow_security_group_id);',
'',
'  -- Set Block-Level Triggers across Migration Project',
'  wwv_mig_frm_utilities.set_blktrig_applicability(',
'                       p_project_id => :MW_PROJ_ID,',
'                       p_security_group_id => :flow_security_group_id);',
'',
'  -- Set Item-Level Triggers across Migration Project',
'  wwv_mig_frm_utilities.set_itemtrig_applicability(',
'                       p_project_id => :MW_PROJ_ID,',
'                       p_security_group_id => :flow_security_group_id); */',
'',
'',
'END;'))
);
