,p_plugin_type=>'REGION TYPE'
,p_name=>'COM.ORACLE.FLOTBARCHART2'
,p_display_name=>unistr('Gr\00E1fico de Barras Flutuantes v2 **')
,p_supported_ui_types=>'DESKTOP'
,p_supported_component_types=>'APEX_APPLICATION_PAGE_ITEMS'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','COM.ORACLE.FLOTBARCHART2'),'#IMAGE_PREFIX#plugins/com.oracle.apex.flot_bar/2.0/')
,p_javascript_file_urls=>wwv_flow_string.join(wwv_flow_t_varchar2(
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/excanvas.min.js',
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/jquery.flot.min.js',
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/jquery.flot.resize.min.js',
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/jquery.flot.categories.min.js',
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/jquery.flot.stack.min.js',
'#IMAGE_PREFIX#libraries/jquery-flot/0.8.2/jquery.flot.axislabels.js',
'#PLUGIN_FILES#histogram.js',
'#PLUGIN_FILES#com.oracle.apex.flot_bar.js'))
,p_css_file_urls=>'#PLUGIN_FILES#com.oracle.apex.flot_bar.css'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function render ',
'(',
'    p_region                in  apex_plugin.t_region,',
'    p_plugin                in  apex_plugin.t_plugin,',
'    p_is_printer_friendly   in  boolean ',
')',
'return apex_plugin.t_region_render_result',
'is',
'    -- Assign readable names to plugin attributes. Omit data attributes, they''ll be handled in ajax function.',
'    -- Dimensions',
'    c_min_height            constant number         := nvl(p_region.attribute_18, 100);',
'    c_max_height            constant number         := nvl(p_region.attribute_19, 500);',
'    c_spacing               constant number         := nvl(p_region.attribute_16, 20);',
'    c_inner_spacing         constant number         := nvl(p_region.attribute_17, 20);',
'',
'    -- Axis titles',
'    c_x_axis_title          constant varchar2(200)  := p_region.attribute_08;',
'    c_y_axis_title          constant varchar2(200)  := p_region.attribute_09;',
'',
'    -- Tooltip configuration',
'    c_show_tooltip          constant boolean        := p_region.attribute_10 is not null;',
'    c_series_tooltip        constant boolean        := instr('':'' || p_region.attribute_10 || '':'', '':SERIES:'') > 0;',
'    c_x_tooltip             constant boolean        := instr('':'' || p_region.attribute_10 || '':'', '':X:'') > 0;',
'    c_y_tooltip             constant boolean        := instr('':'' || p_region.attribute_10 || '':'', '':Y:'') > 0;',
'    c_custom_tooltip        constant boolean        := instr('':'' || p_region.attribute_10 || '':'', '':CUSTOM:'') > 0;',
'',
'    -- Legend',
'    c_show_legend           constant boolean        := p_region.attribute_12 is not null;',
'',
'    -- Display modes',
'    c_value_template        constant varchar2(200)  := nvl(p_region.attribute_15, ''#VALUE#'');',
'    c_horizontal            constant boolean        := nvl(p_region.attribute_25, ''VERTICAL'') = ''HORIZONTAL'';',
'    c_display               constant varchar2(200)  := p_region.attribute_06;',
'',
'    -- Colors',
'    c_color_scheme          constant varchar2(200)  := p_region.attribute_13;',
'    l_colors                varchar2(200)           := p_region.attribute_14;',
'    c_split_series          constant boolean        := (nvl(p_region.attribute_03, ''N'') = ''N'') and (nvl(p_region.attribute_24, ''N'') = ''Y'');',
'',
'    -- Aspect ratios',
'    c_min_ar                constant number         := nvl( apex_plugin_util.get_attribute_as_number( p_plugin.attribute_02, ''Min Aspect Radio'' ), 1.333 );',
'    c_max_ar                constant number         := nvl( apex_plugin_util.get_attribute_as_number( p_plugin.attribute_01, ''Max Aspect Radio'' ), 3 );',
'',
'    -- Function constants',
'    c_rgb_list_regex        constant varchar2(200)  := ''^#[0-9a-fA-F]{6}(,#[0-9a-fA-F]{6})*$'';',
'begin',
'    -- Add placeholder div',
'    sys.htp.p (',
'        ''<div class="a-FlotBar" id="'' || p_region.static_id || ''_container">'' ||',
'            ''<div class="a-FlotBar-desc"></div>'' ||',
'            ''<div class="a-FlotBar-chart" id="'' || p_region.static_id || ''_chart"></div>'' ||',
'        ''</div>'' );',
'',
'    -- Color scheme',
'    case c_color_scheme',
'        when ''MODERN'' then',
'            l_colors := ''#FF3B30,#FF9500,#FFCC00,#4CD964,#34AADC,#007AFF,#5856D6,#FF2D55,#8E8E93,#C7C7CC'';',
'        when ''MODERN2'' then',
'            l_colors := ''#1ABC9C,#2ECC71,#4AA3DF,#9B59B6,#3D566E,#F1C40F,#E67E22,#E74C3C'';',
'        when ''SOLAR'' then',
