,p_plug_name=>unistr('ORDS RESTful \670D\52D9\5B9A\7FA9 (\82E5\9078\53D6\6703\555F\52D5\9801\9762\5C0E\89BD)')
,p_region_name=>'Tree'
,p_region_css_classes=>'a-PageDesigner-treeWrap a-TreeView--ords'
,p_region_template_options=>'#DEFAULT#'
