'    e wwv_flow_global.vc_arr2;',
'begin',
'',
'    l_selected_modules := :P6_MODULES;',
'    c := wwv_flow_utilities.string_to_table2(:P6_MODULES);',
'',
'    for j in 1..c.count loop',
'        if not wwv_flow_utilities.is_numeric(c(j)) then',
