'         c002 = :P160_PRIVILEGE_NAME);',
'    ',
'   ',
'-- Get the roles assinged to the privilege and prepare as colon delimted string',
'    l_roles := '''';',
'    FOR r in (select n002',
'                from wwv_flow_collections ',
'               where collection_name = ''ORDS_PRIVILEGE_ROLES''',
'                 and n001 = l_id)',
'    LOOP',
'        l_roles := l_roles ||r.n002||'':'';',
'    END LOOP;',
'    l_roles := rtrim(l_roles,'':'');',
'',
'-- Get the modules assinged to the privilege and prepare as colon delimted string',
'    l_modules := '''';',
'    FOR r in (select n002',
