prompt ...apex_application_page_chart_s

create or replace view apex_application_page_chart_s
as
select
    f.workspace,
    f.workspace_display_name,
    f.application_id,
    f.application_name,
    c.page_id                            page_id ,
    (select name
     from wwv_flow_steps
     where id = c.page_id
     and flow_id = c.flow_id)            page_name,
    c.region_id                          region_id,
    (select plug_name
     from wwv_flow_page_plugs
     where id = c.region_id
     and flow_id = c.flow_id)            region_name,
    --
    cs.chart_id                          chart_id,
    cs.id                                series_id,
    cs.seq                               series_seq,
    cs.name                              series_name,
    --
    decode(cs.data_source_type,
      'SQL_QUERY','SQL Query',
      'FUNCTION_RETURNING_SQL_QUERY','PL/SQL Function Body returning SQL Query',
      'REGION_SOURCE', 'Region Source',
      'TABLE',       'Table',
    cs.data_source_type)                 data_source_type,
    cs.data_source                       data_source,
    cs.max_row_count                     max_row_count,
    cs.ajax_items_to_submit              items_to_submit,
    --
    cs.location                          location_code,
    decode( cs.location,
       'LOCAL',       'Local Database',
       'REMOTE',      'Remote Database',
       'WEB_SOURCE',  'Web Source' )     location,
    cs.query_owner                       table_owner,
    cs.query_table                       table_name,
    cs.query_where                       where_clause,
    cs.query_order_by                    order_by_clause,
    decode( cs.source_post_processing,
       'SQL', 'SQL Query',
       'WHERE_ORDER_BY_CLAUSE', 'Where/Order By Clause',
       'FUNC_BODY_RETURNING_SQL', 'PL/SQL Function Body returning SQL Query' ) source_post_processing,
    decode(cs.include_rowid_column,
    'Y','Yes',
    'N','No',
    cs.include_rowid_column)             include_rowid_column,
    cs.optimizer_hint,
    --
    (select name
       from wwv_remote_servers
      where id = cs.remote_server_id)    remote_database_name,
    cs.remote_server_id                  remote_database_id,
    (select name
       from wwv_flow_web_src_modules
      where id = cs.web_src_module_id)   web_source_module_name,
    cs.web_src_module_id                 web_source_module_id,
    --
    cs.external_filter_expr,
    cs.external_order_by_expr,
    --
    decode(cs.series_type,
      'area','Area',
      'auto','Automatic',
      'areaRange','Area Range',
      'bar','Bar',
      'barRange','Bar Range',
      'line','Line',
      'lineWithArea','Line with Area',
      'candlestick','Candlestick',
      'boxPlot', 'Box Plot',
      'gantt','Gantt',
    cs.series_type)                       series_type,
    cs.series_name_column_name            series_name_column_name,
    cs.items_value_column_name            items_value_column_name,
    cs.items_label_column_name            items_label_column_name,
    cs.items_short_desc_column_name       items_short_desc_column_name,
    cs.items_low_column_name              items_low_column_name,
    cs.items_high_column_name             items_high_column_name,
    cs.items_open_column_name             items_open_column_name,
    cs.items_close_column_name            items_close_column_name,
    cs.items_volume_column_name           items_volume_column_name,
    cs.items_x_column_name                items_x_column_name,
    cs.items_y_column_name                items_y_column_name,
    cs.items_z_column_name                items_z_column_name,
    cs.items_target_value                 items_target_value,
    cs.items_min_value                    items_min_value,
    cs.items_max_value                    items_max_value,
    cs.group_name_column_name             group_name_column_name,
    cs.group_short_desc_column_name       group_short_desc_column_name,
    cs.custom_column_name                 custom_column_name,
    --
    cs.aggregate_function                 aggregate_function,
    --
    cs.color                              color,
    cs.q2_color                           q2_color,
    cs.q3_color                           q3_color,
    decode(cs.line_style,
      'dashed','Dashed',
      'dotted','Dotted',
      'solid','Solid',
    cs.line_style)                        line_style,
    cs.line_width                         line_width,
    decode(cs.line_type,
      'auto','Automatic',
      'centeredStepped','Centered Stepped',
      'centeredSegmented','Centered Segmented',
      'curved','Curved',
      'segmented','Segmented',
      'stepped','Stepped',
      'straight','Straight',
      'none','None',
    cs.line_type)                         line_type,
    --
    decode(cs.marker_rendered,
    'auto','Automatic',
    'on','On',
    'off','Off',
    cs.marker_rendered)                   marker_rendered,
    decode(cs.marker_shape,
      'auto','Automatic',
      'circle','Circle',
      'diamond','Diamond',
      'plus','Plus',
      'triangleDown','Triangle Down',
      'triangleUp','Triangle Up',
      'human','Human',
      'square','Square',
    cs.marker_shape)                      marker_shape,
    cs.assigned_to_y2                     assigned_to_y2,
    --
    decode(cs.items_label_rendered,
    'Y','Yes',
    'N','No',
    cs.items_label_rendered)              items_label_rendered,
    decode(cs.items_label_position,
      'center','Center',
      'aboveMarker','Above Marker',
      'belowMarker','Below Marker',
      'beforeMarker','Before Marker',
      'afterMarker','After Marker',
      'insideBarEdge','Inside Bar Edge',
      'outsideBarEdge','Outside Bar Edge',
      'auto','Automatic',
    cs.items_label_position)              items_label_position,
    decode(cs.items_label_display_as,
           'LABEL', 'Label',
           'VALUE', 'Value',
           'PERCENT','Percentage Value',
           cs.items_label_display_as)     items_label_display_as,
    cs.items_label_css_classes            items_label_css_classes,
    --
    cs.gantt_start_date_source,
    cs.gantt_start_date_column,
    cs.gantt_start_date_item,
    cs.gantt_end_date_source,
    cs.gantt_end_date_column,
    cs.gantt_end_date_item,
    cs.gantt_row_id,
    cs.gantt_row_name,
    cs.gantt_task_id,
    cs.gantt_task_name,
    cs.gantt_task_start_date,
    cs.gantt_task_end_date,
    cs.gantt_task_css_style,
    cs.gantt_task_css_class,
    cs.gantt_predecessor_task_id,
    cs.gantt_successor_task_id,
    cs.gantt_baseline_start_column,
    cs.gantt_baseline_end_column,
    cs.gantt_baseline_css_class,
    cs.gantt_progress_column,
    cs.gantt_progress_css_class,
    cs.gantt_viewport_start_source,
    cs.gantt_viewport_start_column,
    cs.gantt_viewport_start_item,
    cs.gantt_viewport_end_source,
    cs.gantt_viewport_end_column,
    cs.gantt_viewport_end_item,
    decode (cs.task_label_position,
       'end','End',
       'innerCenter', 'Inner Center',
       'innerEnd','Inner End',
       'innerStart','Inner Start',
       'none','None',
       'start','Start',
       cs.task_label_position)            task_label_position,
    --
    cs.link_target                        link_target,
    decode(cs.link_target_type,
      'REDIRECT_URL','URL',
      'REDIRECT_PAGE','Page in this application',
      'REDIRECT_APP','Page in a different application',
      'DEFINED_BY_DA','Defined by a Dynamic Action',
    cs.link_target_type)                  link_target_type,
    --
    decode(substr(cs.security_scheme,1,1),'!','Not ')||
      nvl((select name
             from wwv_flow_security_schemes
            where to_char(id) = ltrim(cs.security_scheme,'!')
              and flow_id = f.application_id),
    cs.security_scheme)                   authorization_scheme,
    cs.security_scheme                    authorization_scheme_id,
    (select case
              when cs.required_patch > 0
                then PATCH_NAME
              else '{Not '||PATCH_NAME||'}'
            end PATCH_NAME
       from wwv_flow_patches
      where id =abs(cs.REQUIRED_PATCH))   build_option,
    cs.required_patch                     build_option_id,
    --
    cs.series_comment                     series_comment,
    nvl((select r
           from apex_standard_conditions
          where d = cs.DISPLAY_WHEN_COND_TYPE),
    cs.DISPLAY_WHEN_COND_TYPE)            condition_type,
    cs.display_when_cond_type             condition_type_code,
    cs.DISPLAY_WHEN_CONDITION             condition_expression1,
    cs.DISPLAY_WHEN_CONDITION2            condition_expression2,
    cs.updated_on                         last_updated_on,
    cs.updated_by                         last_updated_by,
    --
    cs.name
    ||' seq='||lpad(cs.seq,5,'00000')
    ||' q='||sys.dbms_lob.substr(cs.data_source,20,1)||'.'||sys.dbms_lob.getlength(cs.data_source)
    ||' max='||cs.max_row_count
    ||' link='||substr(cs.link_target,1,20) component_signature
from wwv_flow_jet_chart_series cs,
     wwv_flow_jet_charts       c,
     wwv_flow_authorized       f
where f.workspace_id      = c.security_group_id
  and f.application_id    = c.flow_id
  and c.security_group_id = cs.security_group_id
  and c.flow_id           = cs.flow_id
  and c.id                = cs.chart_id
/

comment on table  apex_application_page_chart_s                               is 'Identifies a chart series associated with a chart on a Page and Region.';
comment on column apex_application_page_chart_s.workspace                     is 'A work area mapped to one or more database schemas.';
comment on column apex_application_page_chart_s.workspace_display_name        is 'Display name for the workspace.';
comment on column apex_application_page_chart_s.application_id                is 'Application Primary Key, Unique over all workspaces.';
comment on column apex_application_page_chart_s.application_name              is 'Identifies the application.';
comment on column apex_application_page_chart_s.page_id                       is 'ID of the application page.';
comment on column apex_application_page_chart_s.page_name                     is 'Name of the application page.';
comment on column apex_application_page_chart_s.region_id                     is 'Identifies the Page Region foreign key to the apex_application_page_regions view.';
comment on column apex_application_page_chart_s.region_name                   is 'Identifies the region name in which this chart series is displayed.';
comment on column apex_application_page_chart_s.chart_id                      is 'Foreign Key of the chart.';
comment on column apex_application_page_chart_s.series_id                     is 'Primary Key of the chart series';
comment on column apex_application_page_chart_s.series_seq                    is 'Identifies the series sequence to determine the order of evaluation';
comment on column apex_application_page_chart_s.series_name                   is 'Chart series name';
comment on column apex_application_page_chart_s.data_source                   is 'The data source that will return the data to display the chart series';
comment on column apex_application_page_chart_s.data_source_type              is 'The data source type';
comment on column apex_application_page_chart_s.max_row_count                 is 'Defines the maximum number of rows to query.';
comment on column apex_application_page_chart_s.items_to_submit               is 'Identifies the page items which should be submitted when an Ajax call is performed for the chart series.';
comment on column apex_application_page_chart_s.location_code                 is 'Identifies the internal code of LOCATION';
comment on column apex_application_page_chart_s.location                      is 'Identifies whether the data source of the region is the local database, a remote database or a web source module';
comment on column apex_application_page_chart_s.table_owner                   is 'Denotes the table owner for TABLE query types';
comment on column apex_application_page_chart_s.table_name                    is 'Denotes the table name for TABLE query types';
comment on column apex_application_page_chart_s.where_clause                  is 'Identifies the WHERE clause provided by the developer and to be applied to TABLE or WEB SOURCE data sources.';
comment on column apex_application_page_chart_s.order_by_clause               is 'Identifies the ORDER BY clause provided by the developer and to be applied to TABLE or WEB SOURCE data sources';
comment on column apex_application_page_chart_s.source_post_processing        is 'Identifies type of SQL post processing for data from external data sources';
comment on column apex_application_page_chart_s.include_rowid_column          is 'Identifies if the pseudo column ROWID should be selected';
comment on column apex_application_page_chart_s.optimizer_hint                is 'Optimizer hint which is added to the region source SQL statement when it is executed';
comment on column apex_application_page_chart_s.remote_database_id            is 'Identifies the primary key of the remote database definition';
comment on column apex_application_page_chart_s.remote_database_name          is 'Identifies the name of the remote database';
comment on column apex_application_page_chart_s.web_source_module_id          is 'Identifies the primary key of the web source module';
comment on column apex_application_page_chart_s.web_source_module_name        is 'Identifies the web source module name';
comment on column apex_application_page_chart_s.external_filter_expr          is 'Filter expression to be sent to the external data source.';
comment on column apex_application_page_chart_s.external_order_by_expr        is 'Order By expression to be sent to the external data source.';
comment on column apex_application_page_chart_s.series_type                   is 'Chart series type: Bar, Area, Line, Line with Area, Candlestick, Bar Range, Area Range, or Automatic, which will automatically set the series type based upon the chosen chart type';
comment on column apex_application_page_chart_s.series_name_column_name       is 'Identifies the "Series Name" column from a multi-series query';
comment on column apex_application_page_chart_s.items_value_column_name       is 'Depending on the chosen chart type, identifies the "Value" column for the chart.  Only for Bar, Line, Area, Combination, Pie, Funnel,Pyramid, and Dial Gauge charts.';
comment on column apex_application_page_chart_s.items_label_column_name       is 'Identifies the "Label" column for the chart, for those chart types that do not use the "Groups" attribute in Oracle JET Charts.';
comment on column apex_application_page_chart_s.items_short_desc_column_name  is 'Identifies the column used to display a custom tooltip on the data point(s) of the chart.';
comment on column apex_application_page_chart_s.items_low_column_name         is 'Identifies the column used to display the low value on a Bar Range, Area Range, or Candlestick chart.';
comment on column apex_application_page_chart_s.items_high_column_name        is 'Identifies the column used to display the high value on a Bar Range, Area Range, or Candlestick chart.';
comment on column apex_application_page_chart_s.items_open_column_name        is 'Identifies the column used to display the opening value on a Stock chart.';
comment on column apex_application_page_chart_s.items_close_column_name       is 'Identifies the column used to display the closing value on a Stock chart.';
comment on column apex_application_page_chart_s.items_volume_column_name      is 'Identifies the column used to display the volume value on a Stock chart.';
comment on column apex_application_page_chart_s.items_x_column_name           is 'Identifies the column used to display the X value on a Bubble or Scatter chart.';
comment on column apex_application_page_chart_s.items_y_column_name           is 'Identifies the column used to display the Y value on a Bubble or Scatter chart.';
comment on column apex_application_page_chart_s.items_z_column_name           is 'Identifies the column used to display the Z value on a Bubble or Scatter chart.';
comment on column apex_application_page_chart_s.items_target_value            is 'Identifies the column used to display the target value on a Funnel chart.';
comment on column apex_application_page_chart_s.items_min_value               is 'Identifies the column used to display the minimum value on a Dial Gauge chart.';
comment on column apex_application_page_chart_s.items_max_value               is 'Identifies the column used to display the maximum value on a Dial Gauge chart.';
comment on column apex_application_page_chart_s.group_name_column_name        is 'Identifies the column used to display a tooltip on the series name(s) displayed on the chart legend.';
comment on column apex_application_page_chart_s.group_short_desc_column_name  is 'Identifies the column used to display a tooltip on the x-axis label(s) of the chart, for accessibility purposes.';
comment on column apex_application_page_chart_s.custom_column_name            is 'Identifies the custom column used to display a more detailed tooltip on the chart or custom colors based on a database value.';
comment on column apex_application_page_chart_s.aggregate_function            is 'Identifies the aggregate function to apply to the value column before rendering the chart.';
comment on column apex_application_page_chart_s.color                         is 'Identifies the color to be used to represent the items of the chart series.';
comment on column apex_application_page_chart_s.q2_color                      is 'Identifies the color to be used to represent the q2 segment of a Box Plot chart series.';
comment on column apex_application_page_chart_s.q3_color                      is 'Identifies the color to be used to represent the q3 segment of a Box Plot chart series.';
comment on column apex_application_page_chart_s.line_style                    is 'Identifies the style of the line to be rendered on the chart.';
comment on column apex_application_page_chart_s.line_width                    is 'Identifies the width of the line to be rendered on the chart.';
comment on column apex_application_page_chart_s.line_type                     is 'Identifies the type of line to be rendered on the chart.';
comment on column apex_application_page_chart_s.marker_rendered               is 'Identifies whether the marker is to be rendered on the chart.';
comment on column apex_application_page_chart_s.marker_shape                  is 'Identifies the shape of the marker displayed on the chart.';
comment on column apex_application_page_chart_s.assigned_to_y2                is 'Identifies whether the series is assigned to the extra Y axis on the chart.';
comment on column apex_application_page_chart_s.items_label_rendered          is 'Identifies whether the label is rendered on a chart item.';
comment on column apex_application_page_chart_s.items_label_position          is 'Identifies the position of the label on a chart item.';
comment on column apex_application_page_chart_s.items_label_display_as        is 'Identifies the format of the label on the pie chart item';
comment on column apex_application_page_chart_s.items_label_css_classes       is 'Identifies the CSS classes used to display the label on a chart item.';
comment on column apex_application_page_chart_s.gantt_start_date_source       is 'Identifies the data source for the Gantt chart start date.';
comment on column apex_application_page_chart_s.gantt_start_date_column       is 'Identifies the column used to display the Gantt chart start date.';
comment on column apex_application_page_chart_s.gantt_start_date_item         is 'Identifies the page item used to define the Gantt chart start date.';
comment on column apex_application_page_chart_s.gantt_end_date_source         is 'Identifies the data source for the Gantt chart end date.';
comment on column apex_application_page_chart_s.gantt_end_date_column        is 'Identifies the column used to display the Gantt chart end date.';
comment on column apex_application_page_chart_s.gantt_end_date_item           is 'Identifies the page item used to define the Gantt chart end date.';
comment on column apex_application_page_chart_s.gantt_row_id                  is 'Identifies the column used to display the row ID on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_row_name                is 'Identifies the column used to display the row name on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_id                 is 'Identifies the column used to display the task ID on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_name               is 'Identifies the column used to display the task name on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_start_date         is 'Identifies the column used to display the task start date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_end_date           is 'Identifies the column used to display the task end date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_css_style          is 'Identifies the column used to display the task css style on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_task_css_class          is 'Identifies the CSS class used to style the task bar on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_predecessor_task_id     is 'Identifies the column used to display the predecessor task ID on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_successor_task_id       is 'Identifies the column used to display the successor task ID on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_baseline_start_column   is 'Identifies the column used to display the baseline start date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_baseline_end_column     is 'Identifies the column used to display the baseline end date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_baseline_css_class      is 'Identifies the CSS class used to style the baseline bar on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_progress_column         is 'Identifies the column used to display the progress value on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_progress_css_class      is 'Identifies the CSS class used to style the progress bar on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_viewport_start_source   is 'Identifies the data source for the Gantt chart viewport start date.';
comment on column apex_application_page_chart_s.gantt_viewport_start_column   is 'Identifies the column used to display the viewport start date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_viewport_start_item     is 'Identifies the page item used to define the Gantt chart viewport start date.';
comment on column apex_application_page_chart_s.gantt_viewport_end_source     is 'Identifies the data source for the Gantt chart viewport end date.';
comment on column apex_application_page_chart_s.gantt_viewport_end_column     is 'Identifies the column used to display the viewport end date on the gantt chart.';
comment on column apex_application_page_chart_s.gantt_viewport_end_item       is 'Identifies the page item used to define the Gantt chart viewport end date.';
comment on column apex_application_page_chart_s.task_label_position           is 'Specifies the position of the task label on the gantt chart.';
comment on column apex_application_page_chart_s.link_target                   is 'Action link for the chart series, used when chart item selected.';
comment on column apex_application_page_chart_s.link_target_type              is 'Defines the type of Action link for the chart series is to be used.';
comment on column apex_application_page_chart_s.authorization_scheme          is 'Identifies an Authorization Scheme that will be applied to the chart series query';
comment on column apex_application_page_chart_s.authorization_scheme_id       is 'Identifies an Authorization Scheme foreign key';
comment on column apex_application_page_chart_s.build_option                  is 'Chart Series query will be processed if the Build Option is enabled';
comment on column apex_application_page_chart_s.build_option_id               is 'Identifies the primary key of this component';
comment on column apex_application_page_chart_s.series_comment                is 'Identifies the series comment.';
comment on column apex_application_page_chart_s.condition_type                is 'Identifies the condition type that must be met in order for the chart series to be rendered or processed.';
comment on column apex_application_page_chart_s.condition_type_code           is 'Identifies the condition type code that must be met in order for the chart series to be rendered or processed.';
comment on column apex_application_page_chart_s.condition_expression1         is 'Identifies the attribute that allows you to conditionally control whether or not a component is rendered or processed.';
comment on column apex_application_page_chart_s.condition_expression2         is 'Identifies the second attribute that allows you to conditionally control whether or not a component is rendered or processed.';
comment on column apex_application_page_chart_s.last_updated_by               is 'APEX developer who made last update.';
comment on column apex_application_page_chart_s.last_updated_on               is 'Date of last update.';
comment on column apex_application_page_chart_s.component_signature           is 'Identifies attributes defined at a given component level to facilitate application comparisons.';

prompt ...apex_application_page_chart_a

create or replace view apex_application_page_chart_a
as
select
    f.workspace,
    f.workspace_display_name,
    f.application_id,
    f.application_name,
    c.page_id                            page_id,
    (select name
       from wwv_flow_steps
      where id = c.page_id
        and flow_id = c.flow_id)         page_name,
    --
    ca.chart_id                          chart_id,
    ca.static_id                         static_id,
    ca.id                                axis_id,
    decode(ca.axis,
    'x','X Axis',
    'y','Y Axis',
    'y2','Extra Y Axis',
    'major','Major Axis',
    'minor','Minor Axis',
    ca.axis)                             axis,
    ca.title                             axis_title,
    decode(ca.is_rendered,
    'on','On',
    'off','Off',
    ca.is_rendered)                      is_rendered,
    --
    ca.min                               axis_minimum,
    ca.max                               axis_maximum,
    decode(ca.format_type,
    'date-short', 'Short Date format',
    'date-medium', 'Medium Date format',
    'date-long', 'Long Date format',
    'date-full', 'Full Date format',
    'time-short', 'Short Time format',
    'time-medium', 'Medium Time format',
    'time-long', 'Long Time format',
    'time-full', 'Full Time format',
    'datetime-short', 'Short Datetime format',
    'datetime-medium', 'Medium Datetime format',
    'datetime-long','Long Datetime format',
    'datetime-full','Full Datetime format',
    'decimal','Decimal',
    'currency','Currency',
    'percent','Percent',
    ca.format_type)                     axis_format_type,
    ca.decimal_places                   axis_decimal_places,
    ca.currency                         axis_currency,
    ca.numeric_pattern                  axis_numeric_pattern,
    decode(ca.format_scaling,
    'auto','Automatic',
    'none','None',
    'thousand','Thousand',
    'million','Million',
    'billion','Billion',
    'trillion','Trillion',
    'quadrillion','Quadrillion',
    ca.format_scaling)                  axis_format_scaling,
    decode(ca.scaling,
    'linear','Linear',
    'log','Log',
    ca.scaling)                         axis_scaling,
    decode(ca.baseline_scaling,
    'min','Minimum',
    'zero','Zero',
    ca.baseline_scaling)                axis_baseline_scaling,
    ca.step                             axis_step,
    decode(ca.position,
    'start','Start',
    'end','End',
    'top','Top',
    'bottom','Bottom',
    'auto','Automatic',
    ca.position)                        axis_position,
    decode(ca.major_tick_rendered,
    'auto','Automatic',
    'on','On',
    'off','Off',
    ca.major_tick_rendered)             axis_major_tick_rendered,
    ca.min_step                         axis_minimum_step,
    decode(ca.minor_tick_rendered,
    'auto','Automatic',
    'on','On',
    'off','Off',
    ca.minor_tick_rendered)             axis_minor_tick_rendered,
    ca.minor_step                       axis_minor_step,
    decode(ca.tick_label_rendered,
    'auto','Automatic',
    'on','On',
    'off','Off',
    ca.tick_label_rendered)             axis_tick_label_rendered,
    decode(ca.tick_label_rotation,
    'auto','Automatic',
    'none','None',
    ca.tick_label_rotation)             axis_tick_label_rotation,
    decode(ca.tick_label_position,
    'inside','Inside',
    'outside','Outside',
    ca.tick_label_position)             axis_tick_label_position,
    decode(ca.split_dual_y,
    'auto','Automatic',
    'on','On',
    'off','Off',
    ca.split_dual_y)                    y2_axis_split_dual,
    ca.splitter_position                y2_axis_splitter_position,
    --
    decode (ca.axis_scale,
      'seconds','Seconds',
      'minutes','Minutes',
      'hours','Hours',
      'days','Days',
      'weeks','Weeks',
      'months','Months',
      'quarters','Quarters',
      'years','Years',
      ca.axis_scale)                    axis_scale,
    decode(ca.zoom_order_seconds,
      'Y','Yes',
      'N','No',
      ca.zoom_order_seconds)            zoom_order_seconds,
    decode(ca.zoom_order_minutes,
      'Y','Yes',
      'N','No',
      ca.zoom_order_minutes)            zoom_order_minutes,
    decode(ca.zoom_order_hours,
      'Y','Yes',
      'N','No',
      ca.zoom_order_hours)              zoom_order_hours,
    decode(ca.zoom_order_days,
      'Y','Yes',
      'N','No',
      ca.zoom_order_days)               zoom_order_days,
    decode(ca.zoom_order_weeks,
      'Y','Yes',
      'N','No',
      ca.zoom_order_weeks)              zoom_order_weeks,
    decode(ca.zoom_order_months,
      'Y','Yes',
      'N','No',
      ca.zoom_order_months)             zoom_order_months,
    decode(ca.zoom_order_quarters,
      'Y','Yes',
      'N','No',
      ca.zoom_order_quarters)           zoom_order_quarters,
    decode(ca.zoom_order_years,
      'Y','Yes',
      'N','No',
      ca.zoom_order_years)              zoom_order_years,
    --
    ca.updated_on                       last_updated_on,
    ca.updated_by                       last_updated_by,
    --
    ca.title
    ||' axis='||ca.axis
    ||' max='||ca.max
    ||' min='||ca.min
    ||' position='||ca.position
    ||' format_type='||ca.format_type component_signature
from wwv_flow_jet_chart_axes ca,
     wwv_flow_jet_charts     c,
     wwv_flow_authorized     f
where f.workspace_id      = c.security_group_id
  and f.application_id    = c.flow_id
  and c.security_group_id = ca.security_group_id
  and c.flow_id           = ca.flow_id
  and c.id                = ca.chart_id
/

comment on table  apex_application_page_chart_a                               is 'Identifies a chart axis associated with a chart on a Page and Region.';
comment on column apex_application_page_chart_a.workspace                     is 'A work area mapped to one or more database schemas.';
comment on column apex_application_page_chart_a.workspace_display_name        is 'Display name for the workspace.';
comment on column apex_application_page_chart_a.application_id                is 'Application Primary Key, Unique over all workspaces.';
comment on column apex_application_page_chart_a.application_name              is 'Identifies the application.';
comment on column apex_application_page_chart_a.page_id                       is 'ID of the application page.';
comment on column apex_application_page_chart_a.page_name                     is 'Name of the application page.';
comment on column apex_application_page_chart_a.chart_id                      is 'Foreign Key of the chart.';
comment on column apex_application_page_chart_a.axis_id                       is 'Primary Key of the chart axis.';
comment on column apex_application_page_chart_a.axis                          is 'The chart axis, whether it is the X, Y, Extra Y, Major or Minor axis of the chart. The major and minor axes only apply to a Gantt chart.';
comment on column apex_application_page_chart_a.axis_title                    is 'The title of the chart axis.';
comment on column apex_application_page_chart_a.is_rendered                   is 'Identifies whether the chart axis is rendered on the chart.';
comment on column apex_application_page_chart_a.axis_minimum                  is 'Identifies the minimum value of the chart axis.';
comment on column apex_application_page_chart_a.axis_maximum                  is 'Identifies the maximum value of the chart axis.';
comment on column apex_application_page_chart_a.axis_format_type              is 'The format type to be used to represent the axis values.';
comment on column apex_application_page_chart_a.axis_decimal_places           is 'Number of decimal places to use in axis values.';
comment on column apex_application_page_chart_a.axis_currency                 is 'The ISO 4217 alphabetic currency code used to represent monetary axis values.';
comment on column apex_application_page_chart_a.axis_numeric_pattern          is 'The numeric pattern used to represent numeric axis values.';
comment on column apex_application_page_chart_a.axis_format_scaling           is 'The scaling behavior of the values on the axis.';
comment on column apex_application_page_chart_a.axis_scaling                  is 'The scaling used to represent numeric axis values.';
comment on column apex_application_page_chart_a.axis_baseline_scaling         is 'Defines whether the axis baseline starts at the minimum value of the data or at zero. Only applies to numerical data axes.';
comment on column apex_application_page_chart_a.axis_step                     is 'The increment between major tick marks. Defaults to null for automatic calculation based on the data. Only applies to time and numerical axes.';
comment on column apex_application_page_chart_a.axis_position                 is 'The position of the axis relative to its content. For vertical charts, only start and end apply. For horizontal charts, only top and bottom apply.';
comment on column apex_application_page_chart_a.axis_major_tick_rendered      is 'Defines whether the major tick marks are rendered.';
comment on column apex_application_page_chart_a.axis_minimum_step             is 'The minimum increment between major tick marks. This is typically used to prevent fractional axis values for discrete measures.';
comment on column apex_application_page_chart_a.axis_minor_tick_rendered      is 'Defines whether the minor tick marks are rendered.';
comment on column apex_application_page_chart_a.axis_minor_step               is 'The increment between minor tick marks. Defaults to null for automatic calculation based on the data.';
comment on column apex_application_page_chart_a.axis_tick_label_rendered      is 'Defines whether the tick labels are rendered.';
comment on column apex_application_page_chart_a.axis_tick_label_rotation      is 'Defines whether the chart will automatically rotate the labels by 90 degrees in order to fit more labels on the axis. The rotation will only be applied to categorical labels for a horizontal axis.';
comment on column apex_application_page_chart_a.axis_tick_label_position      is 'Defines the position of the tick labels relative to the plot area.';
comment on column apex_application_page_chart_a.y2_axis_split_dual            is 'Defines whether the plot area is split into two sections, so that sets of data assigned to the different Y-axes appear in different parts of the plot area. Stock charts do not support "off".';
comment on column apex_application_page_chart_a.y2_axis_splitter_position     is 'In a split dual-Y chart, specifies the fraction of the space that is given to the Y-axis subchart. Valid values are numbers from 0 to 1.';
comment on column apex_application_page_chart_a.axis_scale                    is 'Identifies the type of scale to be applied to the gantt chart';
comment on column apex_application_page_chart_a.zoom_order_seconds            is 'Identifies whether the gantt chart can zoom to seconds time interval';
comment on column apex_application_page_chart_a.zoom_order_minutes            is 'Identifies whether the gantt chart can zoom to minutes time interval';
comment on column apex_application_page_chart_a.zoom_order_hours              is 'Identifies whether the gantt chart can zoom to hours time interval';
comment on column apex_application_page_chart_a.zoom_order_days               is 'Identifies whether the gantt chart can zoom to days time interval';
comment on column apex_application_page_chart_a.zoom_order_weeks              is 'Identifies whether the gantt chart can zoom to weeks time interval';
comment on column apex_application_page_chart_a.zoom_order_months             is 'Identifies whether the gantt chart can zoom to months time interval';
comment on column apex_application_page_chart_a.zoom_order_quarters           is 'Identifies whether the gantt chart can zoom to quarters time interval';
comment on column apex_application_page_chart_a.zoom_order_years              is 'Identifies whether the gantt chart can zoom to years time interval';
comment on column apex_application_page_chart_a.last_updated_by               is 'APEX developer who made last update.';
comment on column apex_application_page_chart_a.last_updated_on               is 'Date of last update.';
comment on column apex_application_page_chart_a.component_signature           is 'Identifies attributes defined at a given component level to facilitate application comparisons.';

--
-- Interactive Report Views
--
prompt ...apex_application_page_ir
create or replace view apex_application_page_ir
as
select
f.workspace,
f.workspace_display_name,
f.application_id,
f.application_name,
ir.page_id                   page_id,
ir.id                        interactive_report_id,
ir.region_id                 region_id,
r.plug_name                  region_name,
--
(select count(*) from wwv_flow_worksheet_columns where worksheet_id = ir.id) number_of_columns,
(select count(*) from wwv_flow_worksheet_col_groups where worksheet_id = ir.id) number_of_column_groups,
(select count(*) from wwv_flow_worksheet_rpts where worksheet_id = ir.id and session_id is null and nvl(is_default,'N')='Y' and application_user='APXWS_ALTERNATIVE') number_of_alt_default_reportS,
(select count(*) from wwv_flow_worksheet_rpts where worksheet_id = ir.id and session_id is null and nvl(is_default,'N')='N' and status='PUBLIC') number_of_public_reports,
(select count(*) from wwv_flow_worksheet_rpts where worksheet_id = ir.id and session_id is null and nvl(is_default,'N')='N' and status='PRIVATE') number_of_private_reports,
ir.max_row_count             ,
ir.max_row_count_message     ,
ir.no_data_found_message     ,
ir.max_rows_per_page         ,
ir.search_button_label       ,
r.ajax_items_to_submit       page_items_to_submit, /* Only for compatibility */
ir.base_table_or_view        ,
r.plug_source                sql_query,
--
ir.show_nulls_as             ,
decode(ir.pagination_type,
   'ROWS_X_TO_Y_OF_Z','Row Ranges X to Y of Z (no pagination)',
   'ROWS_X_TO_Y','Row Ranges X to Y (no pagination)',
   'SEARCH_ENGINE','Search Engine 1,2,3,4 (set based pagination)',
   'COMPUTED_BUT_NOT_DISPLAYED','Use Externally Created Pagination Buttons',
   'ROW_RANGES','Row Ranges 1-15 16-30 (with set pagination)',
   'ROW_RANGES_IN_SELECT_LIST','Row Ranges 1-15 16-30 in select list (with pagination)',
   'ROW_RANGES_WITH_LINKS','Row Ranges X to Y of Z (with pagination)',
   'NEXT_PREVIOUS_LINKS','Row Ranges X to Y (with next and previous links)',
   ir.pagination_type)       pagination_scheme,
decode(ir.pagination_display_position,
  'BOTTOM_LEFT','Bottom - Left',
  'BOTTOM_RIGHT','Bottom - Right',
  'TOP_LEFT','Top - Left',
  'TOP_RIGHT','Top - Right',
  'TOP_AND_BOTTOM_LEFT','Top and Bottom - Left',
  'TOP_AND_BOTTOM_RIGHT','Top and Bottom - Right',
  ir.pagination_display_position)       pagination_display_position,
  cast(null as varchar2(10))            button_template,
--
decode(ir.show_finder_drop_down ,'Y','Yes','N','No',ir.show_finder_drop_down ) show_finder_drop_down ,
decode(ir.report_list_mode,'TABS', 'Yes','No') show_reports_select_list,
decode(ir.show_display_row_count,'Y','Yes','N','No',ir.show_display_row_count) show_display_row_count,
decode(ir.show_search_bar       ,'Y','Yes','N','No',ir.show_search_bar       ) show_search_bar       ,
decode(ir.show_search_textbox   ,'Y','Yes','N','No',ir.show_search_textbox   ) show_search_textbox   ,
decode(ir.show_actions_menu     ,'Y','Yes','N','No',ir.show_actions_menu     ) show_actions_menu     ,
cast(null as varchar2(10))  actions_menu_icon,
cast(null as varchar2(10))  finder_icon,
decode(ir.show_select_columns,'Y','Yes','N','No',ir.show_select_columns) show_select_columns,
decode(ir.show_rows_per_page ,'Y','Yes','N','No',ir.show_rows_per_page)  show_rows_per_page,
decode(ir.show_filter        ,'Y','Yes','N','No',ir.show_filter        ) show_filter        ,
decode(ir.show_sort          ,'Y','Yes','N','No',ir.show_sort          ) show_sort          ,
decode(ir.show_control_break ,'Y','Yes','N','No',ir.show_control_break ) show_control_break ,
decode(ir.show_highlight     ,'Y','Yes','N','No',ir.show_highlight     ) show_highlight     ,
decode(ir.show_computation   ,'Y','Yes','N','No',ir.show_computation   ) show_compute       ,
decode(ir.show_aggregate     ,'Y','Yes','N','No',ir.show_aggregate     ) show_aggregate     ,
decode(ir.show_chart         ,'Y','Yes','N','No',ir.show_chart         ) show_chart         ,
decode(ir.show_group_by      ,'Y','Yes','N','No',ir.show_group_by      ) show_group_by      ,
decode(ir.show_pivot         ,'Y','Yes','N','No',ir.show_pivot         ) show_pivot         ,
decode(ir.show_notify        ,'Y','Yes','N','No',ir.show_notify        ) show_notify        ,
decode(ir.show_flashback     ,'Y','Yes','N','No',ir.show_flashback     ) show_flashback     ,
decode(ir.allow_report_saving,'Y','Yes','N','No',ir.allow_report_saving) show_save          ,
decode(ir.allow_save_rpt_public,'Y','Yes','N','No',ir.allow_save_rpt_public) show_save_public,
decode(substr(ir.save_rpt_public_auth_scheme,1,1),'!','Not ')||
nvl((select name
 from   wwv_flow_security_schemes
 where  to_char(id) = ltrim(ir.save_rpt_public_auth_scheme,'!')
 and    flow_id = f.application_id),
 ir.save_rpt_public_auth_scheme)  save_public_auth_scheme,
ir.save_rpt_public_auth_scheme    save_public_auth_scheme_id,
decode(ir.show_reset         ,'Y','Yes','N','No',ir.show_reset         ) show_reset         ,
decode(ir.show_download      ,'Y','Yes','N','No',ir.show_download      ) show_download      ,
decode(ir.show_help          ,'Y','Yes','N','No',ir.show_help          ) show_help          ,
ir.download_formats          ,
ir.download_filename         filename,
ir.csv_output_separator      separator,
ir.csv_output_enclosed_by    enclosed_by,
--
decode(ir.show_detail_link,
       'Y', 'Single Row View',
       'C', 'Custom Link target',
       'N', 'No Link Column') detail_link_type,
ir.detail_link                detail_link_target,
ir.detail_link_text           detail_link_text,
ir.detail_link_attr           detail_link_attributes,
ir.detail_link_checksum_type  detail_link_checksum_type,
nvl((select r from apex_standard_conditions where d = ir.detail_link_condition_type),ir.detail_link_condition_type)
                                     detail_link_condition_type,
ir.detail_link_condition_type detail_link_cond_type_code,
ir.detail_link_cond           detail_link_cond_expression,
ir.detail_link_cond2          detail_link_cond_expression2,
decode(substr(ir.detail_link_auth_SCHEME,1,1),'!','Not ')||
nvl((select name
 from   wwv_flow_security_schemes
 where  to_char(id) = ltrim(ir.detail_link_auth_scheme,'!')
 and    flow_id = f.application_id),
 ir.detail_link_auth_scheme)  detail_link_auth_scheme,
ir.detail_link_auth_scheme    detail_link_auth_scheme_id,
--
ir.alias                      alias,
ir.report_id_item             ,
ir.max_query_cost             ,
ir.email_from                 ,
decode(ir.fixed_header,
    'NONE',     'None',
    'PAGE',     'Page',
    'REGION',   'Region',
    ir.fixed_header) fixed_header,
ir.fixed_header_max_height,
--
    decode(icon_view_enabled_yn,'Y','Yes','N','No','No') icon_view_enabled_yn,
    decode(icon_view_use_custom,'Y','Yes','N','No','No') icon_view_use_custom,
    icon_view_custom_link     ,
    icon_view_link_column     ,
    icon_view_img_src_column  ,
    icon_view_label_column    ,
    icon_view_img_attr_text   ,
    icon_view_alt_text        ,
    icon_view_title_text      ,
    icon_view_columns_per_row ,
    --
    decode(detail_view_enabled_yn,'Y','Yes','N','No','No') detail_view_enabled_yn,
    detail_view_before_rows,
    detail_view_for_each_row,
    detail_view_after_rows,
--
ir.internal_uid,
--
ir.created_on,
ir.created_by,
ir.updated_on,
ir.updated_by,
--
'Interactive Report'||
' rc='||ir.max_row_count||
' '||length(ir.max_row_count_message)||
length(ir.no_data_found_message)||
ir.max_rows_per_page||
substr(ir.search_button_label,1,30)||
length(r.ajax_items_to_submit)||
substr(ir.show_nulls_as,1,30)||
' p='||ir.pagination_type||
' '||ir.pagination_display_position||
' opt='||ir.show_finder_drop_down||
ir.show_display_row_count||
ir.show_search_bar||
ir.show_search_textbox||
ir.show_actions_menu||
ir.show_select_columns||
ir.show_rows_per_page||
ir.show_filter||
ir.show_sort||
ir.show_control_break||
ir.show_highlight||
ir.show_computation||
ir.show_aggregate||
ir.show_chart||
ir.show_group_by||
ir.show_pivot||
ir.show_notify||
ir.show_flashback||
ir.allow_report_saving||
ir.allow_save_rpt_public||
ir.save_rpt_public_auth_scheme||
ir.show_reset||
ir.show_download||
ir.show_help||
' rpts='||ir.report_list_mode||
' dl='||substr(ir.download_formats,1,40)||
' fn='||substr(ir.download_filename,1,30)||
ir.csv_output_separator||
ir.csv_output_enclosed_by||
ir.show_detail_link||
' l='||substr(ir.detail_link,1,30)||
' lt='||substr(ir.detail_link_text,1,30)||
substr(ir.detail_link_attr,1,30)||
ir.detail_link_checksum_type||
' lc='||ir.detail_link_condition_type||
length(ir.detail_link_cond)||
length(ir.detail_link_cond2)||
nvl((select name n from wwv_flow_security_schemes where to_char(id) = ltrim(ir.detail_link_auth_SCHEME,'!') and flow_id = f.application_id),'Not found')||
' a='||substr(ir.alias,1,30)||
ir.report_id_item||
ir.max_query_cost||
ir.email_from
component_signature
--
from wwv_flow_worksheets ir,
     wwv_flow_page_plugs r,
     wwv_flow_authorized f
where f.workspace_id       = ir.security_group_id
  and f.application_id     = ir.flow_id
  and ir.security_group_id = r.security_group_id(+)
  and ir.flow_id           = r.flow_id(+)
  and ir.region_id         = r.id(+)
/

comment on table  apex_application_page_ir                      is 'Identifies attributes of an interactive report';
comment on column apex_application_page_ir.workspace            is 'A work area mapped to one or more database schemas';
comment on column apex_application_page_ir.workspace_display_name is 'Display name for the workspace';
comment on column apex_application_page_ir.application_id       is 'Application Primary Key, Unique over all workspaces';
comment on column apex_application_page_ir.application_name     is 'Identifies the application';
comment on column apex_application_page_ir.page_id              is 'Identifies page number';
comment on column apex_application_page_ir.interactive_report_id        is 'ID of the interactive report';
comment on column apex_application_page_ir.region_id                    is 'ID of the interactive report region';
comment on column apex_application_page_ir.region_name                  is 'Name of the interactive report region';
comment on column apex_application_page_ir.number_of_columns            is 'Total number of columns returned by the query for this report region';
comment on column apex_application_page_ir.number_of_column_groups      is 'Number of defined column groups for this report region';
comment on column apex_application_page_ir.number_of_alt_default_reports is 'Number of developer-defined alternative default reports for this report region';
comment on column apex_application_page_ir.number_of_public_reports      is 'Number of user-defined public shared reports for this report region';
comment on column apex_application_page_ir.number_of_private_reports     is 'Number of user-defined private reports for this report region';

comment on column apex_application_page_ir.max_row_count                is 'Maximum number of rows to query before sorting';
comment on column apex_application_page_ir.max_row_count_message        is 'Message to display if the maximum number of rows is exceeded';
comment on column apex_application_page_ir.no_data_found_message        is 'Message to display when no rows are returned by the report query';
comment on column apex_application_page_ir.max_rows_per_page            is 'Maximum number of rows to display on a single HTML page.  The Rows select list will not display any values higher than this amount.';
comment on column apex_application_page_ir.search_button_label          is 'Text to use for the search button label';
comment on column apex_application_page_ir.page_items_to_submit         is 'List of page items values which are submitted when the search button is clicked';
comment on column apex_application_page_ir.base_table_or_view           is 'Table or View used as the base for this interactive report region.';
comment on column apex_application_page_ir.sql_query                    is 'Query used as the base for this interactive report region.  The original query will be preserved as a subquery in the actual SQL used for the report';
comment on column apex_application_page_ir.show_nulls_as                is 'Identifies the text to display for null columns. The default value is "-".';
comment on column apex_application_page_ir.pagination_scheme            is 'Identifies the pagination style to use for this report';
comment on column apex_application_page_ir.pagination_display_position  is 'Identifies the position of pagination links relative to this report';
comment on column apex_application_page_ir.button_template              is 'Obsolete.';
comment on column apex_application_page_ir.show_finder_drop_down        is 'Determines whether to display the drop down to the left of the search field';
comment on column apex_application_page_ir.show_reports_select_list     is 'Determines whether saved reports are displayed as select list in the Search Bar.';
comment on column apex_application_page_ir.show_display_row_count       is 'Determines whether to display the row count selector in the Search Bar';
comment on column apex_application_page_ir.show_search_bar              is 'Determines whether to display the Search Bar for the interactive report';
comment on column apex_application_page_ir.show_search_textbox          is 'Determines whether to allow searching from the Search Bar';
comment on column apex_application_page_ir.show_actions_menu            is 'Determines whether to display the Actions Menu and Actions Menu icon in the Search Bar';
comment on column apex_application_page_ir.actions_menu_icon            is 'Obsolete.';
comment on column apex_application_page_ir.finder_icon                  is 'Obsolete.';
comment on column apex_application_page_ir.show_select_columns          is 'Determines whether to show the Select Columns option in the Actions Menu';
comment on column apex_application_page_ir.show_rows_per_page           is 'Determines whether to show the Rows Per Page option in the Actions Menu';
comment on column apex_application_page_ir.show_filter                  is 'Determines whether to show the Filter option in the Actions Menu';
comment on column apex_application_page_ir.show_sort                    is 'Determines whether to show the Sort option in the Actions Menu';
comment on column apex_application_page_ir.show_control_break           is 'Determines whether to show the Control option in the Actions Menu';
comment on column apex_application_page_ir.show_highlight               is 'Determines whether to show the Highlight option in the Actions Menu';
comment on column apex_application_page_ir.show_compute                 is 'Determines whether to show the Compute option in the Actions Menu';
comment on column apex_application_page_ir.show_aggregate               is 'Determines whether to show the Aggregate option in the Actions Menu';
comment on column apex_application_page_ir.show_chart                   is 'Determines whether to show the Chart option in the Actions Menu';
comment on column apex_application_page_ir.show_group_by                is 'Determines whether to show the Group By option in the Actions Menu';
comment on column apex_application_page_ir.show_pivot                   is 'Determines whether to show the Pivot option in the Actions Menu';
comment on column apex_application_page_ir.show_notify                  is 'Determines whether to show the Notify option in the Actions Menu';
comment on column apex_application_page_ir.show_flashback               is 'Determines whether to show the Flashback option in the Actions Menu';
comment on column apex_application_page_ir.show_save                    is 'Determines whether to show the Save Report option in the Actions Menu';
comment on column apex_application_page_ir.show_save_public             is 'Determines whether to show the Public option in the Save Report dialog';
comment on column apex_application_page_ir.save_public_auth_scheme      is 'This authorization scheme must evaluate to TRUE in order for Public option to display in Save Report dialog';
comment on column apex_application_page_ir.save_public_auth_scheme_id   is 'Foreign Key';
comment on column apex_application_page_ir.show_reset                   is 'Determines whether to show the Reset option in the Actions Menu';
comment on column apex_application_page_ir.show_download                is 'Determines whether to show the Download option in the Actions Menu';
comment on column apex_application_page_ir.show_help                    is 'Determines whether to show the Help option in the Actions Menu';
comment on column apex_application_page_ir.download_formats             is 'Identifies the download formats supported for this interactive report';
comment on column apex_application_page_ir.filename                     is 'Identifies the filename prefix used when this report data is downloaded';
