create or replace view apex_application_page_ir_pivot
as
select
f.workspace,
f.workspace_display_name,
f.application_id,
f.application_name,
r.page_id             page_id,
r.worksheet_id        interactive_report_id,
p.websheet_id         websheet_id,
r.id                  report_id,
r.application_user    application_user,
r.name                report_name,
p.id                  pivot_id,
p.pivot_columns       ,
p.row_columns         ,
p.created_on          ,
p.created_by          ,
p.updated_on          last_updated_on,
p.updated_by          last_updated_by
--
from wwv_flow_worksheet_pivot p,
     wwv_flow_worksheet_rpts r,
     wwv_flow_worksheets ws,
     wwv_flow_authorized    f
where f.workspace_id       = ws.security_group_id
  and f.application_id     = ws.flow_id
  and ws.security_group_id = r.security_group_id
  and ws.id                = r.worksheet_id
  and r.security_group_id  = p.security_group_id
  and r.id                 = p.report_id
/

comment on table  apex_application_page_ir_pivot                            is 'Identifies pivot view defined in user-level report settings for an interactive report';
comment on column apex_application_page_ir_pivot.workspace                  is 'A work area mapped to one or more database schemas';
comment on column apex_application_page_ir_pivot.workspace_display_name     is 'Display name for the workspace';
comment on column apex_application_page_ir_pivot.application_id             is 'Application Primary Key, Unique over all workspaces';
comment on column apex_application_page_ir_pivot.application_name           is 'Identifies the application';
comment on column apex_application_page_ir_pivot.page_id                    is 'Identifies page number';
comment on column apex_application_page_ir_pivot.interactive_report_id      is 'ID of the interactive report';
comment on column apex_application_page_ir_pivot.websheet_id                is 'ID of the Websheet';
comment on column apex_application_page_ir_pivot.report_id                  is 'ID of the report';
comment on column apex_application_page_ir_pivot.application_user           is 'The owner of these pivot settings';
comment on column apex_application_page_ir_pivot.report_name                is 'The name of these report settings';
comment on column apex_application_page_ir_pivot.pivot_id                   is 'ID of this pivot settings';
comment on column apex_application_page_ir_pivot.pivot_columns              is 'Columns to pivot';
comment on column apex_application_page_ir_pivot.row_columns                is 'Columns to display in report row';
comment on column apex_application_page_ir_pivot.created_on                 is 'Auditing; date the record was created.';
comment on column apex_application_page_ir_pivot.created_by                 is 'Auditing; user that created the record.';
comment on column apex_application_page_ir_pivot.last_updated_on            is 'Auditing; date the record was last modified.';
comment on column apex_application_page_ir_pivot.last_updated_by            is 'Auditing; user that last modified the record.';

create or replace view apex_application_page_ir_pvagg
as
select
f.workspace,
f.workspace_display_name,
f.application_id,
f.application_name,
r.page_id             page_id,
r.worksheet_id        interactive_report_id,
p.websheet_id         websheet_id,
r.id                  report_id,
r.application_user    application_user,
r.name                report_name,
p.id                  pivot_id,
a.display_seq         ,
a.function_name       ,
a.column_name         ,
a.db_column_name      ,
a.column_label        ,
a.format_mask         ,
decode(a.display_sum,'Y','Yes','N','No',a.display_sum)
                      display_sum,
a.created_on          ,
a.created_by          ,
a.updated_on          last_updated_on,
a.updated_by          last_updated_by
--
from wwv_flow_worksheet_pivot p,
     wwv_flow_worksheet_pivot_agg a,
     wwv_flow_worksheet_rpts r,
     wwv_flow_worksheets ws,
     wwv_flow_authorized    f
where f.workspace_id       = ws.security_group_id
  and f.application_id     = ws.flow_id
  and ws.security_group_id = r.security_group_id
  and ws.id                = r.worksheet_id
  and r.security_group_id = p.security_group_id
  and r.id                = p.report_id
  and p.security_group_id = a.security_group_id
  and p.id                = a.pivot_id
/

