comment on table apex_component_signatures is 'Identifies two applications using APEX_APPLICATION_1 and APEX_APPLICATION_2 session date, APEX_COMPONENT must be 0 (all), or the name of an APEX component';
comment on column apex_component_signatures.page_id is 'Identifies the page number for application components that are specific to a page';
comment on column apex_component_signatures.page_name is 'Identifies the page name for application components that are specific to a page name';
comment on column apex_component_signatures.component is 'Identifies the application component type';
comment on column apex_component_signatures.component_name is 'Identifies the application component name';
comment on column apex_component_signatures.component_signature is 'Identifies the signature of the components attributes';
comment on column apex_component_signatures.application_1 is 'Returns 1 if the component exists in application 1, returns 0 if not';
comment on column apex_component_signatures.application_2 is 'Returns 1 if the component exists in application 2, returns 0 if not';


create or replace view apex_inst_restadm_clients as
select /*+ NO_XML_QUERY_REWRITE*/
    client_name,
    admin_email,
    client_id,
    client_secret
from xmltable(
    '/apex-rest-clients/client'
    passing sys.xmltype(wwv_flow_instance_rest_admin.client_info_xml)
    columns
        client_name    varchar2(255) path 'name/text()',
        admin_email    varchar2(255) path 'admin-email/text()',
        client_id      varchar2(32)  path 'client-id/text()',
        client_secret  varchar2(32)  path 'client-secret/text()' ),
    wwv_flow_current_sgid sgid
where sgid.has_apex_admin_read_role_yn = 'Y'
/

comment on table apex_inst_restadm_clients is 'Identifies all registered OAuth clients for the APEX REST Administration interface';
comment on column apex_inst_restadm_clients.client_name is 'Identifies the client name';
comment on column apex_inst_restadm_clients.admin_email is 'Identifies the email address of the support contact for the OAuth client';
comment on column apex_inst_restadm_clients.client_id is 'Identifies the Client ID for the OAuth "client_credentials" flow';
comment on column apex_inst_restadm_clients.client_secret is 'Identifies the Client Secret for the OAuth "client_credentials" flow';

create or replace view apex_inst_restadm_client_roles as
select /*+ NO_XML_QUERY_REWRITE*/
    c.client_name,
    c.admin_email,
    r.role_name
from xmltable(
    '/apex-rest-clients/client'
    passing sys.xmltype(wwv_flow_instance_rest_admin.client_info_xml)
    columns
        client_name    varchar2(255)  path 'name/text()',
        admin_email    varchar2(255)  path 'admin-email/text()',
        role_xml       sys.xmltype    path 'client-roles' ) c,
    xmltable (
        '/client-roles/role-name'
        passing c.role_xml
        columns
            role_name varchar2(30) path 'text()' ) r,
    wwv_flow_current_sgid sgid
where sgid.has_apex_admin_read_role_yn = 'Y'
/

comment on table apex_inst_restadm_client_roles is 'Identifies the roles assigned to the registered OAuth clients';
comment on column apex_inst_restadm_client_roles.client_name is 'Identifies the client name';
comment on column apex_inst_restadm_client_roles.admin_email is 'Identifies the email address of the support contact for the OAuth client';
comment on column apex_inst_restadm_client_roles.role_name is 'Identifies the name of the assigned ORDS role';

