prompt ... apex_workspace_credentials

create or replace view apex_workspace_credentials
as
select
    w.workspace,
    w.workspace_display_name,
    --
    c.name,
    c.static_id,
    case c.authentication_type
        when 'BASIC'                     then 'Basic Authentication'
        when 'OAUTH2_CLIENT_CREDENTIALS' then 'OAuth2 Client Credentials flow'
        when 'JWT'                       then 'JSON Web Tokens'
    end                                      credential_type,
    --
    c.last_updated_by,
    c.last_updated_on,
    --
    substr(c.name, 1, 30) || '.' || length(c.name) ||
    ' url=' || substr(c.static_id,1,30) || length(c.static_id)
                                             component_signature
from wwv_credentials c,
     wwv_flow_current_workspaces w
where w.workspace_id         = c.security_group_id
/

comment on table apex_workspace_credentials is 'Identifies the credentials within a workspace';
comment on column apex_workspace_credentials.workspace                    is 'A work area mapped to one or more database schemas';
comment on column apex_workspace_credentials.workspace_display_name       is 'Display name for the workspace';
comment on column apex_workspace_credentials.name                         is 'Name of the stored credentials';
comment on column apex_workspace_credentials.static_id                    is 'Identified the credentials within API calls';
comment on column apex_workspace_credentials.credential_type              is 'Identified the authentication type the credentials can be used for';
comment on column apex_workspace_credentials.last_updated_by              is 'APEX developer who made last update';
comment on column apex_workspace_credentials.last_updated_on              is 'Date of last update';
comment on column apex_workspace_credentials.component_signature          is 'Identifies attributes defined at a given component level to facilitate comparisons';

