prompt ...apex_workspace_restenabled_sql

create or replace view apex_workspace_restenabled_sql
as
select
    w.workspace,
    w.workspace_display_name,
    --
    rs.id                                    remote_server_id,
    rs.name                                  remote_server_name,
    rs.static_id                             remote_server_static_id,
    rs.base_url                              base_url,
    credential_id                            ,
    cs.name                                  credential_name,
    cs.static_id                             credential_static_id,
    --
    rs.ords_timezone                         server_time_zone,
    rs.ords_init_code                        init_plsql_code,
    rs.ords_cleanup_code                     cleanup_plsql_code,
    --
    rs.last_updated_by                       ,
    rs.last_updated_on                       ,
    --
    substr(rs.name, 1, 30) || '.' || length(rs.name) ||
    ' url=' || substr(rs.base_url,1,30) || length(rs.base_url)
                                             component_signature
from wwv_remote_servers rs,
     wwv_credentials cs,
     wwv_flow_current_workspaces w
where w.workspace_id         = rs.security_group_id
  and cs.id (+)              = rs.credential_id
  and rs.server_type         = 'REMOTE_SQL'
/

comment on table apex_workspace_restenabled_sql is 'Remote servers configured within an application';
comment on column apex_workspace_restenabled_sql.workspace                    is 'A work area mapped to one or more database schemas';
comment on column apex_workspace_restenabled_sql.workspace_display_name       is 'Display name for the workspace';
comment on column apex_workspace_restenabled_sql.remote_server_id             is 'REST Enabled SQL service internal ID';
comment on column apex_workspace_restenabled_sql.remote_server_name           is 'Name to identify the REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.remote_server_static_id      is 'Static ID to identify the REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.base_url                     is 'Base HTTP or HTTPS URL for the REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.server_time_zone             is 'Time Zone the REST Enabled SQL service runs in. Important for correct handling of DATE and TIMESTAMP values';
comment on column apex_workspace_restenabled_sql.init_plsql_code              is 'PL/SQL code to be executed remotely as initialization before the component SQL is executed';
comment on column apex_workspace_restenabled_sql.cleanup_plsql_code           is 'PL/SQL code to be executed remotely as cleanup after the component SQL is executed';
comment on column apex_workspace_restenabled_sql.credential_id                is 'Internal ID of the credential store assigned to this REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.credential_name              is 'Name of the credential assigned to this REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.credential_static_id         is 'Static ID of the credential assigned to this REST Enabled SQL service';
comment on column apex_workspace_restenabled_sql.last_updated_by              is 'APEX developer who made last update';
comment on column apex_workspace_restenabled_sql.last_updated_on              is 'Date of last update';
comment on column apex_workspace_restenabled_sql.component_signature          is 'Identifies attributes defined at a given component level to facilitate application comparisons';


