prompt ...apex_appl_data_profiles

create or replace view apex_appl_data_profiles
as
select
    f.workspace,
    f.workspace_display_name,
    f.application_id,
    f.application_name,
    --
    d.id                                     data_profile_id,
    d.name                                   ,
    d.format                                 ,
    d.encoding                               ,
    --
    d.decimal_characters                     ,
    --
    d.row_selector                           ,
    decode(d.is_single_row,
           'Y', 'Yes', 'N', 'No')            is_single_row,
    d.xml_namespaces                         ,
    --
    d.last_updated_by                        ,
    d.last_updated_on                        ,
    --
    substr(d.name, 1, 30) || length(d.name) ||
    ' df=' || substr(d.format, 1, 30)  || ' enc=' || d.encoding ||
    ' dc=' || decimal_characters ||
    ' r=' || substr( d.row_selector, 1, 30 ) || length(d.row_selector)
    as component_signature
from wwv_flow_data_profiles d,
     wwv_flow_authorized    f
where f.workspace_id      = d.security_group_id
  and f.application_id    = d.flow_id
/

comment on table apex_appl_data_profiles is 'Available Data Profiles used for parsing JSON, XML and other data';
comment on column apex_appl_data_profiles.workspace                    is 'A work area mapped to one or more database schemas';
comment on column apex_appl_data_profiles.workspace_display_name       is 'Display name for the workspace';
comment on column apex_appl_data_profiles.application_id               is 'Application Primary Key, Unique over all workspaces';
comment on column apex_appl_data_profiles.application_name             is 'Identifies the application';
comment on column apex_appl_data_profiles.data_profile_id              is 'Internal ID of the data profile';
comment on column apex_appl_data_profiles.name                         is 'Name to identify the profile';
comment on column apex_appl_data_profiles.format                       is 'Data format, typically XML or JSON';
comment on column apex_appl_data_profiles.encoding                     is 'Oracle NLS charset name, which the data is encoded in (e.g. AL32UTF8)';
comment on column apex_appl_data_profiles.decimal_characters           is 'Decimal characters to use in order to parse number values';
comment on column apex_appl_data_profiles.row_selector                 is 'Expression to select rows within the data; depends on data format';
comment on column apex_appl_data_profiles.is_single_row                is 'Indicates whether this data profile returns a single row only^';
