comment on column apex_appl_web_src_parameters.database_operation           is 'Assigned database operation';
comment on column apex_appl_web_src_parameters.name                         is 'Parameter name';
comment on column apex_appl_web_src_parameters.value                        is 'Default or static value for this parameter';
comment on column apex_appl_web_src_parameters.is_required                  is 'Indicates whether this parameter is required';
comment on column apex_appl_web_src_parameters.direction                    is 'Indicates whether this parameter is input, output or both';
