    --
    l.url,
    l.http_method,
    l.content_length,
    l.status_code,
    l.resp_content_length,
    l.elapsed_sec,
    l.request_date,
    l.flow_user     apex_or_db_user
from
    wwv_flow_webservice_log l,
    wwv_flow_current_workspaces w
where l.security_group_id = w.workspace_id
/

comment on table  apex_webservice_log                               is 'Identifies web service requests in the APEX instance.';
comment on column apex_webservice_log.workspace_id                  is 'Primary key that identifies the workspace.';
comment on column apex_webservice_log.workspace_name                is 'Name of the current workspace.';
comment on column apex_webservice_log.workspace_display_name        is 'Display name for the workspace';
comment on column apex_webservice_log.url                           is 'The URL of the web service request.';
comment on column apex_webservice_log.http_method                   is 'The HTTP method used in the web service request.';
comment on column apex_webservice_log.content_length                is 'The length in bytes of the payload of the web service request.';
comment on column apex_webservice_log.status_code                   is 'The HTTP status code for the response.';
comment on column apex_webservice_log.resp_content_length           is 'The length in bytes of the response.';
comment on column apex_webservice_log.elapsed_sec                   is 'The time in seconds from request to response.';
comment on column apex_webservice_log.request_date                  is 'Indicates the date of the web service request.';
comment on column apex_webservice_log.apex_or_db_user               is 'The APEX user that caused the request to be made or the DB user (prefixed with DB:)';
